css:
@-moz-document domain('playingcards.io')
  {
    .CardPile {
        background-color: transparent;
        border-color: transparent;
    }
    .Hand {
        background-color: rgba(255, 255, 255, .5);
    }
     .Card:hover>* {
        position: relative;
        transform: scale(1.05);
    }
    .Hand__empty-text {
        color: #888 ;
    }
  }
