css:
@-moz-document domain("thecointribune.com"), domain("cointribune.com") {
/* General styles */
.uk-section-default{ background: #111 !important;}
.tm-headerbar-top, .uk-navbar-container, .uk-section-muted, .uk-navbar-dropdown, .uk-card-default{ background: #202020 !important;}

div#widget-custom_html-3 a:first-child, .uk-navbar-nav>li>a, .uk-section-muted, #footer .subMenuFooter a, .uk-navbar-dropdown, .uk-navbar-dropdown-nav>li>a, .uk-text-emphasis, .uk-card-default, .uk-h3, .uk-h2{ color: #fff !important;}

.uk-navbar-dropdown-dropbar{ margin: 0 !important;}
}

@-moz-document domain("thecointribune.com"), domain("cointribune.com") {
/* Articles */

html, .uk-article-title, em{ color: #fff !important;}
.sidebarNewsletter{ color: #000 !important;}
}

@-moz-document domain("thecointribune.com"), domain("cointribune.com") {
/* AdBlocker */
#blocsRaccourcis, .adv-link, .sidebarPartenaires{ display: none !important;}
}
