css:
#toolbar-context-menu:hover, menupopup menu:hover, menupopup menuitem:hover, popup menu:hover, popup menuitem:hover, 
#bookmarks-menu autorepeatbutton:hover, #personal-bookmarks autorepeatbutton:hover {color: #fff000 !important;}

:not(menulist) > menupopup > menuitem,
menupopup > menu, popup > menuitem, popup > menu { padding-left: 0px !important; padding-right: 0 !important;}
menupopup .menu-text, popup .menu-text { margin-left: -26px !important;}
menupopup .menu-iconic-text, popup .menu-iconic-text { margin-left: 0px !important;}
menupopup .menu-iconic-left,popup .menu-iconic-left { width: 0px !important; margin-left: 2px !important; margin-right: 2px !important;}
menupopup, .menupopup, popup, .popup {-moz-appearance: none !important; background: #000  !important;
-moz-border-left-colors: none !important; -moz-border-right-colors: none !important; -moz-border-top-colors: none !important; 
-moz-border-bottom-colors: none !important; -moz-border-radius: 0px !important;}

/* --- Schrift --- */
menupopup > menu,menupopup > menuitem,popup > menu,popup > menuitem {-moz-appearance: none !important;color: #fff !important;
font-weight: bold !important;font-size: 14.5px !important;min-height: 18px !important;height: 24px !important;
margin: 0px 0px 1px 0px !important;}

/* --- Hintergrund ---*/

#toolbar-context-menu, menuitem, menupopup, popup, menupopup menu, menupopup menuitem, popup menu, popup menuitem, 
#bookmarks-menu autorepeatbutton,#personal-bookmarks autorepeatbutton { background:#000 !important; 
border-top: 0px  !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important;}

/* --- Highlightbalken ---*/
menupopup > menu[_moz-menuactive="true"]:not([disabled="true"]),menupopup > menuitem[_moz-menuactive="true"]:not([disabled="true"]),
popup > menu[_moz-menuactive="true"]:not([disabled="true"]),popup > menuitem[_moz-menuactive="true"]:not([disabled="true"]),
#personal-bookmarks menupopup > menuitem[_moz-menuactive="true"]:not([disabled="true"]),
#personal-bookmarks menupopup > menu[_moz-menuactive="true"]:not([disabled="true"]) {color: #fff !important;opacity: 1.2 !important;
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAACCAIAAAD91JpzAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAFklEQVR4nGPcycDAwMDA6M7AwMjAAAALNQEEU8MqlQAAAABJRU5ErkJggg==") !important;}



/* --- Ordner ---*/
#PersonalToolbar toolbarbutton[class="bookmark-item"][type="menu"],menu[container="true"][class="menu-iconic bookmark-item"]
{ list-style-image:  url("data:image/png;base64, ") !important;
-moz-image-region: rect(-3px, 20px, 20px, -3px) !important;}

/* --- Pfeile ---*/
menupopup menu > .menu-right {background: url("data:image/png;base64,") no-repeat center center !important;}

/* ---- Pfeile / HÃ¶he + Breite --- */
popup menu > .menu-right,popup menupopup menu > .menu-right, menupopup >menu >.menu-right {-moz-appearance: none !important;
min-width: 28px !important; max-width: 28px !important; min-height: 28px !important; max-height: 28px !important;
margin: 6px 0px 0px 0px !important; opacity: 1 !important;}
