css:
@-moz-document domain("index-education.net") {
/* General background */
div[id="GInterface.Instances[2]"], .dotty{ background: #111 !important;}


/* Background widgets */
.widget.travailafaire, .widget.edt, .widget.notes, .widget.menudelacantine, .widget.discussions, .widget.actualites, .widget.agenda, .widget.viescolaire, .widget.ressourcepedagogique, .widget.ds, .widget.enseignementadistance, div[id="div"], .UtilitaireBloc_containerInverse
{
    background-color: #202020 !important;
}


/* Widgets text primary color */
.widget.travailafaire div.content-container, .as-info-light, .widget.edt td.Insecable div.ie-ellipsis, .widget.edt .PetitEspaceDroit, .widget.notes h4, .widget.notes span.date, .widget.agenda li h4, .widget.agenda li span, .widget.ressourcepedagogique li h4, .widget.ressourcepedagogique li span.date, .widget.menudelacantine li, .widget.menudelacantine .legende > span, div.Texte, .widget.ds, .widget.enseignementadistance, .widget.viescolaire li, .PetitEspaceBas, .EspaceDroit div, .Espace .switch label
{ 
    color: #fff !important;
}


/* Secondary color */
ul.sub-liste li div.as-header h5, div.liste-docs a, .widget.travailafaire header h3, .widget.ressourcepedagogique header h3, .widget.ressourcepedagogique li a.file-name, .widget.ds header h3, .widget.ds h4, .widget.viescolaire li h4
{ 
    color: cornflowerblue !important;
}
.widget.notes h3{ color: #c54800 !important;}


/* Grades */
div[id="GInterface.Instances[2].Instances[1]_piedDeListe"] span{ color: #fff !important;}


/* Timetable */
div[id="GInterface.Instances[2].Instances[1].Instances[0]_Contenu_TitreHaut"] td div.ie-ellipsis, div[id="GInterface.Instances[2].Instances[1].Instances[0]_Contenu_TitreLignes"] .PetitEspaceDroit
{ 
    color: #fff !important;
}

/* Communication */
.BlocPastille, .BlocPastille .Espace, .BlocPastille div div{ background-color: #202020 !important;}
.PetitEspaceGauche, .UtilitaireBloc_itemInverse:nth-child(1) div, .BlocPastille .Actu_Descriptif{ color: #fff !important;}
}
