css:
@-moz-document domain("fujiya-avic.co.jp") {
/* body の基本文字サイズ小さすぎない？ */
body {
    font-size: 16px;
    font-family: 'Noto Sans CJK JP', sans-serif;
}

/* ヘッダの文字小さすぎない？ */
.block-headernav {
    font-size: 1.4rem;
    margin-left: 15px;
}

/* フッターは崩れるのでそのまま */
.block-footer-main-nav {
    font-size: 1.3rem;
}

/* なぜデスクトップ版トップページにモバイル版と同じ巨大なスライダーをつけたがるのか… */
.block-top-body--body {
    height: 220px;
}
.block-top-body--body .slider-item {
    width: 220px;
}
.slick-counter-inner {
    bottom: 0px;
}
#top-slider .slick-prev,
#top-slider .slick-next {
    top: 202px;
}

/* フジヤレッド彩度低減し目に優しく */
.pane-globalnav-inner,
.block-global-search--search-detail-link,
.-lBtn_kdwr01 .-lBtn_kdwr01_ul li > a {
    background-color: #aa000e;
}

/** 左カラム **/
.block-genre-tree--items.block-genre-tree--level1 > .block-genre-tree--item > a {
    line-height: 2em;
    font-size: 1em;
}
.block-genre-tree--item > span {
    font-size: 1em;
}

/* ブランド名 英文字／カタカナ改行整形 */
.block-genre-tree--item a {
    display: inline;
}
.block-genre-tree--item a,
.brand_search-list-brand_name-list a,
.alphabet-target-inner,
.block-genre-tree--level-2.block-visible a {
    display: block;
    width: 100%;
    padding-right: 20px;
    line-height: 1.2em;
}
.brand_search-list-brand_name-list .txt-en::after,
.alphabet-target-inner .txt-en::after,
.block-genre-tree--level-2.block-visible a .txt-en::after {
    content: "";
    display: block;
}

.alphabet-target-inner .txt-ja,
.block-genre-tree--level-2.block-visible a .txt-ja {
    font-feature-settings: "palt";
}

/** トップページメインカラム **/
/* トップページ上 Topics */
.-topTit01 {
    margin-bottom: 16px;
}
.-secTpoics01_ul01 {
    font-feature-settings: "palt";
}
.-secTpoics01 .-secTpoics01_ul01 > li {
    margin-bottom: 0.75em;
    font-size: 0.95em;
}

/* トップページ 告知枠 */
.-secAttention01 a {
    text-decoration: none;
}
.-secAttention01 a:hover {
    border-bottom: solid 1px #aa000e
}


/* トップページ下 お知らせ */
.block-top-topic--header {
    margin-bottom: 5px;
}
.block-top-topic--items a dd {
    text-decoration: none;
    font-feature-settings: "palt";
}

.block-top-topic--items li {
    font-size: 0.95em;
    padding: 10px 16px 5px;
    border-bottom: none;
}

/* 検索条件枠でかすぎ問題 */
.block-detailed_search-content {
    line-height: 1.2em;
    padding: 18px 24px;
}
.block-detailed_search-scroll {
    height: 125px;
}
.block-selectbox,
.block-detailed_search-slider-input {

    height: 2em;
}
.block-selectbox select {
    padding: 0 20px 0 5px;
}

.block-detailed_search-box .block-detailed_search-scroll.brand {
    background-color: #F8F9FA
}

.block-detailed_search-brand,
.block-detailed_search-checkbox {
    font-size: 0.8em;
}
.mCSB_container.mCS_y_hidden.mCS_no_scrollbar_y .block-detailed_search-checkbox li {
    display: inline-block;
    margin-right: 1em;
}

.block-detailed_search-selectbox .block-selectbox select,
.block-detailed_search-keyword input,
.block-detailed_search-slider-input input[type='text'] {
    font-size: 0.85em;
    height: 35px;
}
.block-detailed_search-keyword button {
    height: 35px;
    width: 35px;
}
.block-detailed_search-checkbox label::before,
.block-detailed_search-radio label::before {
    width: 20px;
    height: 20px;
}
.block-detailed_search-box h3 {
    margin-bottom: 10px;
}


/* 商品名とか価格とか文字小さすぎない？ */
.-ul_detail01 > li > a > .-rnkName,
.-ul_detail01 > li > a > .-rnkPrc {
    font-size: 1.5rem;
}

/** 商品一覧 リスト表示 文字サイズとか余白とか **/
/* パンくずリスト */
.block-topic-path--list li {
    font-size: 1.2em;
}

/* 商品間区切り */
.block-pickup-list-p li + li {
    margin-top: 30px;
    padding-top: 30px;
}

/* 各商品部 */
.block-pickup-list-p--goods-brand .txt-en {
    font-size: 1em;
    color: #888;
}
.block-pickup-list-p--goods-name {
    margin-top: 0;
}
.block-pickup-list-p--goods-name a {
    font-size: 1.2em;
    line-height: 1.2em;
    font-feature-settings: "palt";
}
/* バリエーション名 */
.block-pickup-list-p .variation-name {
    margin-top: 0;
    font-size: 0.7em;
    color: #8F8F90;
    font-feature-settings: "palt";
}
/* ピクアップコメント */
.block-pickup-list-p--comment {
    margin-top: 4px;
    font-size: 0.85em;
}
.-txt_cmnt1_detail01 {
    padding: 0.5em 0;
    color: #aa000e;
}

.block-goods-stock + .block-pickup-list-p--price-items,
.block-goods-duein + .block-pickup-list-p--price-items {
    margin-top: -6px;
}
.block-pickup-list-p--price-infos {
    margin-top: 8px;
    padding-top: 8px;
}

/* 商品一覧 リスト表示／グリッド表示 文字サイズとか余白とか */
.block-category-top-nav .block-goods-list--sort-order-items dt {
    display: block;
    height: 2.3em;
}

.block-pickup-list-p--goods-condition,
.block-thumbnail-t--goods-condition {
    color: #aa000e;
}

.block-thumbnail-t--goods-brand .txt-en {
    color: #666;
}
.block-pickup-list-p--goods-brand .txt-ja,
.block-thumbnail-t--goods-brand .txt-ja {
    font-size: 0.7em;
    margin-top: 0;
    color: #999;
}
.block-pickup-list-p--goods-name,
.block-thumbnail-t--goods-name {
    margin-top: 4px;
}
.block-thumbnail-t--goods-name a {
    color: #000;
    font-size: 1.1em;
    line-height: 1.2em;
    font-feature-settings: "palt";
}
.block-thumbnail-t .variation-name,
.block-thumbnail-t--goods-stock,
.block-thumbnail-t--price-infos {
    margin-top: 1px;
    font-feature-settings: "palt";
}
.block-pickup-list-p .price,
.block-thumbnail-t--goods .price {
    font-size: 1.1em;
}

/* フッター */
.block-footer-company-inner dl {
    font-size: 0.85em;
    line-height: 1.25em;
}
.block-footer-main-nav-list-items {
    font-feature-settings: "palt";
}

/** 買取／カテゴリ／ブランド一覧展開メニュー **/
.block-globalnav-layer-2nd-list > li > a {
    font-weight: 700;
    display: block;
    margin-bottom: 20px;
    line-height: 1.2em;
}
.block-globalnav-layer-2nd-list > li > ul > li {
    font-size: 1em;
}

.block-globalnav-layer-2nd-list li + li {
    margin-top: 1em;
}

/** 商品ページ **/
.block-goods-name h2 {
    line-height: 1.4em;
    margin: 4px 0;
}

.goods-detail-description {
    font-size: 0.9em;
}
.goods-detail-description dt {
    min-width: 6em;
}
.goods-detail-description dd {
    font-feature-settings: "palt";
}
.block-goods-brand {
    margin-bottom: 10px;
}
.block-goods-brand .txt-en {
    font-size: 1.2em;
    color: #666;
}

/* 買取価格表 */
.block-assessment--price-items {
    font-size: 0.85em;
}

/* 商品説明 */
.pane-goods-center strong {
    font-size: 1.5em;
    line-height: 1.6em;
}
.pane-goods-center b {
    font-size: 1.2em;
    line-height: 2.4em;
    font-feature-settings: "palt";
}
.pane-goods-center p {
    font-size: 0.95em;
}
.pane-goods-center b + br:after {
    content: "";
    display: block;
    margin-bottom: 5px;
}

/* 中古商品のコンディション */
table.-detailTbl01 {
    width: 100%;
}

.-detailTbl01 caption {
    font-size: 0.9em;
}

.-detailTbl01 tr th {
    color: #6a2934;
    font-size: 0.9em;
    font-weight: 600;
}

.-detailTbl01 tr td {
    font-size: 0.9em;
    word-wrap: break-word;
    padding: 0 0 0 1em;
    font-weight: 300;
}
.-tbl02 td {
    text-align: left;
    font-feature-settings: "palt";
}
}
