css:
@-moz-document url-prefix(https://www.wolvden.com/) {


#main, .main {
    background:#2d2033!important;
}

body {
    background:url('https://i.imgur.com/7kB7Veq.png')!important;
    background-attachment:fixed!important;
    background-size:100%!important;
    background-color:#0a070b!important;
    color:gray!important;
}

h1,
h2,
h3 {
    color:gray!important;
}
a:link,
a:visited {
    color:gray!important;
}
a:hover,
a:active {
    color:white!important;
    transition:all ease 2s;
}
/* Nav */

.nav-tabs .nav-item.show .nav-link,
.nav-tabs .nav-link.active {
    color:gray!important;
    background:black!important;
}
.breadcrumb-nav {background:#100f10!important;
    color:gray!important;
}

/* Sidebar */

#sidebar, .index .sidebar {
    background:#1d1b1e!important;
    color:gray!important;
}
#sidebar .card {border-bottom:none!important;}

/* Main Tables */
.table td {
    background:#392548!important;
    color:gray!important;
}
.table .subheader,
.pup {
    background:#565d72!important;
    color:black!important;
}
th, th.top {
    background:#100f10!important;
    color:gray!important;
}

/* Other */
.card {
    color:gray!important;
    background:#433547!important;
}

.btn {
    background:#1c1b1e!important;
    color:white;
    border:1px solid black;
    transition:all ease 1s;
}
.btn:hover {
    background:#584663!important;
    color:white;
    border:1px solid black;
    transition:all ease 1s;
}
.btn-info {
    background:#1d1b1e!important;
    color:white;
    border:1px solid black;
    transition:all ease 1s;
}
.btn-info:hover {
    background:#564663!important;
    color:black;
    border:1px solid black;
    transition:all ease 1s;
}
.form-control {
    background:#1d1b1e!important;
    color:gray;
    transition:all ease 1s;
    border:none!important;
}

.form-control:focus {
    background:#2f2136!important;
    color:gray;
    transition:all ease 1s;
    border:none!important;
}

.note-editor.note-airframe .note-editing-area .note-editable, .note-editor.note-frame .note-editing-area .note-editable {
	background:#1d1b1e!important;
    color:gray;
    border:1px solid black;
    transition:all ease 1s;
	}

#header .navbar-status {background:#1d1b1e;
    color:gray;
    border:none!important;}

/* Chat Bar */

.s-chat-message:nth-child(even) {background:#433547!important;
    color:gray!important;}

.s-chat-message:nth-child(odd) {background:#1d1b1e!important;
    color:gray!important;}

#fra_chatContainer #txtChatInput {  background:#1d1b1e!important;
    color:white;
    border:1px solid black;
    transition: all ease 1s;
    border-radius:15px;}

.s-chat-sidebar#label_currentChannel{
    background:#594663!important;
    color:black;
    border:1px solid black;
    transition:all ease 1s;
}

.note-editor.note-airframe .note-statusbar .note-resizebar, .note-editor.note-frame .note-statusbar .note-resizebar {background:black!important;color:white!important;border:none!important;}
.note-editor.note-airframe .note-statusbar, .note-editor.note-frame .note-statusbar {background:black!important;color:white!important;border:none!important;}



