css:
@-moz-document url-prefix("https://www.hoyolab.com/")
{
html, body, .mhy-page-header-mask {
    background-color: #1F1F1F;
    color: #DCDCDC;
}

.header, .mhy-contribution-card__time, .mhy-search-bar-placeholder__item, .mhy-search-bar__input, .mhy-button-header .mhy-button__button, .header-item__button:hover .header-item__icon, .header-item--active .header-item__icon, .header-newpost-dialog__draft, .header-notification-dialog__clear:hover, .mhy-image-tab .mhy-tab__item:hover, .header-notification-list__item:hover, .mhy-contribution-card:hover, .mhy-article-list .mhy-article-card:hover, .editor-container #toolbar.ql-toolbar, .mhy-classification-selector-menu, .mhy-topic-label-selector__add, .mhy-button-normal .mhy-button__button, .mhy-reply-box__textarea .reply-editor .ql-editor, .mhy-contribution-label, .mhy-contribution-post-card:hover, .contribution-status-container .contribution-status-item, .mhy-float-menu ul .mhy-float-menu__item, .mhy-account-center-reply-card, .mhy-game-record-card__item, .mhy-level-info-list, .mhy-table thead, .mhy-table tbody tr:nth-child(even), .mhy-article-card+.mhy-article-card:hover, .mhy-account-center-content .mhy-article-card:first-child:hover,  .reply-card__replies {
    background-color: #3e3e3e;
}

.mhy-search-bar--focused .mhy-search-bar__input, .mhy-input__container--focus {
    background-color: #3e3e3e;
    border-color: #ed781e;
}

.editor-container:focus-within, .mhy-topic-label-selector__add:hover, .mhy-reply-box__textarea .reply-editor .ql-editor:focus {
    border-color: #ed781e;
}

.mhy-select.mhy-select-outlined:hover {
    border: 1px solid #ed781e;
}

.header__wrp, .mhy-side-menu, .mhy-home__post-wrp, .mhy-side-section, .mhy-home-list__header-box--fixed, .mhy-notification-card__origin, .mhy-button-outlined .mhy-button__button, .mhy-account-center-reply-card__origin, .mhy-article-page-author, .mhy-follow-button.mhy-follow-button-lg, .mhy-article-actions, .mhy-article-page-reply-action, .reply-card .reply-card-inner-reply-operation, .edit-link-sheet .mhy-input .mhy-input__container, .form-item-container__describe .mhy-input__maxtip, input, .mhy-float-dialog, .mhy-page-header-wrp, .mhy-contribution-card, .mhy-autocomplete__result, .mhy-article-card, .mhy-post-new, .mhy-selectmenu, .mhy-article-list__empty, .mhy-strategy-list, .header-notification-dialog__clear, .mhy-article-card__data-item.mhy-article-card__data-item-clickable>a:hover::before, .mhy-article-card__data-item.mhy-article-card__data-item-clickable>div:hover::before, .mhy-container, .mhy-dialog__content, .mhy-article-page-author-header .mhy-page-header-mask, .mhy-reply-box__submit.mhy-button-normal.mhy-button--disabled .mhy-button__button, .mhy-reply-list, .mhy-contribution .contribution-content-container, .mhy-contribution .contribution-article-list-header, .mhy-contribution .contribution-article-list .mhy-article-list__body {
    background-color: #313131;
}

.mhy-container, .mhy-home__post-wrp, .mhy-side-section, .mhy-layout__subtitle, .mhy-article-list__empty, .mhy-op-card__content, .mhy-topic-card__header, .header__logo .mhy-icon, .mhy-side-menu__item, .mhy-contribution-card__title, .mhy-account-title__name, .mhy-article-card__title>span:last-child, .mhy-button-header .mhy-button__button, .mhy-strategy-card__title h2, input, .header-newpost-dialog__draft, .header-notification-list .mhy-account-title__name, .header-account-menu__title, .editor-container #toolbar.ql-toolbar button .mhy-icon, .mhy-input__container input, .mhy-input__container textarea, .mhy-img-text-article.ql-snow .ql-editor p, .mhy-simple-editor .ql-snow .ql-editor p, .form-item-container, .mhy-select.mhy-select-outlined .mhy-select__val, .mhy-button-normal .mhy-button__button, .mhy-dialog, .reply-card__content, .mhy-action-menu__label, .reply-card .reply-card-inner-reply__content, .mhy-page-header__right .mhy-action__button .mhy-icon, .mhy-contribution .contribution-content, .mhy-contribution-post-card__title, .mhy-float-menu ul span, a, .mhy-img-article__describe, .mhy-game-record-card__title, .mhy-game-record-card__name, .mhy-select.mhy-select-outlined .mhy-selectmenu__label, .mhy-level-info__exptext, .mhy-account-center .mhy-account-center__action .icon-notice {
    color: #DCDCDC;
}

.mhy-op-card:hover, .mhy-op-card__content:hover, .mhy-topic-list .mhy-topic-card:hover, .mhy-topic-card__header:hover, .mhy-side-menu__item:hover, .mhy-side-menu__item.mhy-side-menu__item--focus, .mhy-tools__item:hover, .mhy-tools__item:hover span, .mhy-recommend-user-list-side .mhy-user-card:hover, .mhy-search-bar-placeholder__item:hover, .header-account-menu-item__content:hover, .header-account-menu-item--active .header-account-menu-item__content {
    background-color: #3e3e3e;
    color: #ed781e !important;
}

.mhy-button.mhy-button-header:not(.mhy-button--disabled):hover .mhy-button__button, .mhy-post-new__post .mhy-button__button:hover .mhy-icon, .mhy-selectmenu__item:hover, .header-newpost-dialog__draft:hover, .editor-container #toolbar.ql-toolbar button .mhy-icon:hover {
    background-color: #3e3e3e;
    color: #ed781e;
}

.mhy-classification-selector-item:hover, .mhy-classification-selector-menu__biz--active {
    background-color: #313131;
    color: #ed781e;
}

.mhy-button.mhy-button-normal:not(.mhy-button--disabled):hover .mhy-button__button {
    background-color: #1F1F1F;
    color: #ed781e;
}

.header__logo .mhy-icon:hover, .mhy-text-tab .mhy-tab__item--active span, .mhy-text-tab .mhy-tab__item--active a, .mhy-article-card__link:hover .mhy-article-card__title>span:last-child, .mhy-strategy-card__title h2:hover, .mhy-account-title__name:hover, .header-item__button:hover .header-item__icon .mhy-icon, .header-item--active .header-item__icon .mhy-icon, .header-newpost-dialog__draft:hover .mhy-icon, .mhy-side-section__more:hover, .mhy-button__button:hover, .mhy-topic-label .mhy-router-link:hover, .mhy-new-article__draft:hover, .mhy-new-article__draft:hover .mhy-icon, .reply-detail-origin:hover, .reply-detail-origin .mhy-icon:hover, .mhy-icon:hover, .mhy-action-menu .mhy-icon:hover, .mhy-img-text-article.ql-snow .ql-editor a:hover, .mhy-simple-editor .ql-snow .ql-editor a:hover, .mhy-page-header__right .mhy-action__button .mhy-icon.icon-header_more:hover, a:hover, .mhy-account-center-header__data-num, .mhy-game-record-card__value, .mhy-select.mhy-select-outlined .mhy-selectmenu__label:hover, .mhy-level-status__exptext, .mhy-level-status__progress-text, .mhy-level-info-log__today-exp, .mhy-level-info-log .mhy-level-info-list dd {
    color: #ed781e;
}

.mhy-article-card+.mhy-article-card {
    border-top: 1px solid #313131;
}

.mhy-img-article__thumb.swiper-slide::after {
    border: 2px solid #ed781e;
}

#icon-notice_reply_hover > path:nth-child(1), #icon-notice_like_hover > path:nth-child(1), #icon-notice_follow_hover > path:nth-child(1), #icon-notice_system_hover > path:nth-child(1), #icon-notice_system_hover > path:nth-child(3), #icon-notice_system_hover > path:nth-child(5), #icon-notice_follow_hover > path:nth-child(3) {
    fill: #ed781e !important;
}

#icon-reply > path:nth-child(1), #icon-fav > path:nth-child(1), #icon-like > path:nth-child(1), #icon-share > path:nth-child(2) {
    fill: #DCDCDC;
}

.reply-card--highlight {
    animation: none;
}

::-webkit-scrollbar-track-piece
  {
    background-color: #212121;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0) !important;
    border-radius: 0 !important;
    border: none !important;
  }

::-webkit-scrollbar {
    background-color: rgba(62, 62, 62, .92);
    border: 1.5px solid rgb(62, 62, 62);
    width: 15px;
}

::-webkit-scrollbar-thumb {
    background-color: rgba(31, 31, 31, 1);
    border: 1.5px solid rgb(62, 62, 62);
}
}
