css:
@-moz-document domain("mtlnovel.com") {
body {
    background-image: none;
    background: #f8f1d9 url("data:image/png;base64,") !important;
}

.m-card {
    border-color: rgba(0, 0, 0, 0);
    box-shadow: none;
    background: #f6edd4 url("data:image/png;base64,") !important;
}

.par {
    letter-spacing: 0;
    font-size: 20px !important;
    font-family: Open Sans,Tahoma,sans-serif !important;
}

footer {
    display: none;
}

.info-wrap, tr.odd, .chapter-nav, button#settings {
    background-color: rgba(0, 0, 0, .05);
}

.box {
    border: 1px solid #ccc;
}

button#settings:hover, .box:hover {
    background-color: rgba(0, 0, 0, .1);
}
}
