css:
@-moz-document domain("brick-hill.com") {
body {
/*[[bkgColorKey1]]*/;
}

/* (defaultRGB) background: linear-gradient(0deg, rgba(114,183,227,1) 0%, rgb(5, 100, 157) 100%) fixed; */
/*(defaultHEX) #72b7e3 #05639d*/


/* Cards */

  nav div.primary {
    background-color: /*[[prmyColor]]*/;
}

  nav div.secondary {
    background-color: /*[[scndColor]]*/;
}

nav .info {
    background-color: /*[[scndColor]]*/;
}
 
.card .content {
    padding: 10px;
    background-color: #fff;
    border-radius: 0px !important
}

.card {
    border-radius: 0px !important;
    box-shadow: 0px 0px 0px 0px #c3cdd8
}

.card .footer {
    border: 1px solid #C3CDD8;
    border-top: 0px;
    padding: 10px;
    background-color: #fff;
}

.content {
    border: 1px solid #C3CDD8;
}

.main-holder {
    padding-top: 30px;
    background-color: /*[[holdercolor]]*/;
    border-radius: 3px;
}

.line, hr {
    border-bottom: 2px solid;
    border-top: 0px solid;
    border-color: #c3cdd88a;
}

.trade-item-box, .trade-item img {
    border: 1px solid #C3CDD8;
    border-radius: 0px;
}


.card .top:not(.absolute) {
    font-weight: 700;
    color: #fff;
    background: rgb(69,187,249);
    background: linear-gradient(0deg, rgba(69,187,249,1) 0%, rgba(73,208,250,1) 100%);
    border: 1px solid;
    border-color: #46BCF9;
	letter-spacing: 0.2px;
}

.thread-label {
    background: linear-gradient(0deg, rgba(73,208,250,1) 0%, rgba(69,187,249,1) 100%);
    border-radius:0px;
    border: 1px solid #46BCF9;
}

.card .top:not(.absolute).blue {
    color: #fff;
    background: rgb(2,123,204);
    background: linear-gradient(0deg, rgba(2,123,204,1) 0%, rgba(65,156,216,1) 100%);
    border-color: #007ACC;
}

.thread-label.blue {
    background: linear-gradient(0deg, rgba(65,156,216,1) 0%, rgba(2,123,204,1) 100%);
    border: 1px solid #007ACC;
}

.card .top:not(.absolute).green {
    color: #fff;
    background: rgb(48,184,71);
    background: linear-gradient(0deg, rgba(48,184,71,1) 0%, rgba(59,223,86,1) 100%);
    border-color: #30B747;
}

.thread-label.green {
    background: linear-gradient(0deg, rgba(59,223,86,1) 0%, rgba(48,184,71,1) 100%);
    border-color: #30B747
}

.card .top:not(.absolute).red {
    color: #fff;
    background: rgb(222,28,34);
    background: linear-gradient(0deg, rgba(222,28,34,1) 0%, rgba(254,87,70,1) 100%);
    border-color: #DD1A21;
}

.thread-label.red {
    background: linear-gradient(0deg, rgba(254,87,70,1) 0%, rgba(222,28,34,1) 100%);
    border-color:#DD1A21
}

.card .top:not(.absolute).orange {
    color: #fff;
    background: rgb(217,125,0);
    background: linear-gradient(0deg, rgba(217,125,0,1) 0%, rgba(243,154,0,1) 100%);
    border-color: #e38b00;
}

.thread-label.orange {
    background: linear-gradient(0deg, rgba(243,154,0,1) 0%, rgba(217,125,0,1) 100%);
    border-color: #e38b00;
}

.card .item-card-details, .card .item-content {
    font-weight: 500;
    background-color: #fff;
    border: 0px solid #C3CDD8;
    padding: 10px;
    padding-left: 0px;
}

.card .thumbnail.dark {
    background-color: #fff;
    border: 1px solid #C3CDD8;
    transition: 0.3s;
}

.card .thumbnail.dark:hover {
    border: 1px solid #46BCF9;
}

.box.shaded, box-item-img {
    background-color: #fff0;
    border: 0px solid;
    border-right: 1px solid #C3CDD8;
    border-radius: 0px;
}

.box.item-img.special {
    border: 3px solid #ffd52d;
}

.profile-card.crate img {
    border: 1px solid #C3CDD8;
    background-color: #fff;
    border-radius: 0px;
}

.item-card-container {
    background-color: #fff;
    border-radius: 0px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0);
    border: 1px solid #C3CDD8;
}

.item-card-container:hover {
    border-color: #30B747;
}

/* Buttons */

.button:not(.plain), button:not(.plain) {
    border-radius: 0px;
    text-transform: uppercase;
    color: #fff;
    font-weight: bold;
    background: rgb(207,207,207);
    background: linear-gradient(180deg, rgba(207,207,207,1) 0%, rgba(184,184,184,1) 100%);
    border: 1px solid #B6B6B6;
}

.button:not(.plain).blue, button:not(.plain).blue {
    color: #fff;
    background: rgb(2,123,204);
    background: linear-gradient(0deg, rgba(2,123,204,1) 0%, rgba(65,156,216,1) 100%);
    border-color: #007ACC;
}

.button:not(.plain).blue:hover, button:not(.plain).blue:hover {
    background: linear-gradient(0deg, rgba(65,156,216,1) 0%, rgba(2,123,204,1) 100%);
}

.button:not(.plain).blue:active, button:not(.plain).blue:active, .button:not(.plain).green:active, button:not(.plain).green:active, .button:not(.plain).red:active, button:not(.plain).red:active, .button:not(.plain).orange:active, button:not(.plain).orange:active {
  transform: translateY(1px);
}

.button:not(.plain).green, button:not(.plain).green {
    color: #fff;
    background: rgb(48,184,71);
    background: linear-gradient(0deg, rgba(48,184,71,1) 0%, rgba(59,223,86,1) 100%);
    border-color: #30B747;
}

.button:not(.plain).green:hover, button:not(.plain).green:Hover {
    background: linear-gradient(0deg, rgba(59,223,86,1) 0%, rgba(48,184,71,1) 100%);
}

.button:not(.plain).red, button:not(.plain).red {
    color: #fff;
    background: rgb(222,28,34);
    background: linear-gradient(0deg, rgba(222,28,34,1) 0%, rgba(254,87,70,1) 100%);
    border-color: #DD1A21;
}

.button:not(.plain).red:hover, button:not(.plain).red:hover {
    background: linear-gradient(0deg, rgba(254,87,70,1) 0%, rgba(222,28,34,1) 100%);
}

.button:not(.plain).orange, button:not(.plain).orange {
    color: #fff;
    background: rgb(217,125,0);
    background: linear-gradient(0deg, rgba(217,125,0,1) 0%, rgba(243,154,0,1) 100%);
    border-color: #e38b00;
}

.button:not(.plain).orange:hover, button:not(.plain).orange:hover {
    background: linear-gradient(0deg, rgba(243,154,0,1) 0%, rgba(217,125,0,1) 100%);
}

.button:not(.plain).bucks, button:not(.plain).bucks {
    color: #fff;
    background: rgb(6,112,33);
    background: linear-gradient(0deg, rgba(6,112,33,1) 0%, rgba(0,151,36,1) 100%);
    border-color: #0B6824;
    text-transform: capitalize;
}

.button:not(.plain).bucks:hover, button:not(.plain).bucks:hover {
    background: linear-gradient(0deg, rgba(0,151,36,1) 0%, rgba(6,112,33,1) 100%);
}

.button:not(.plain).bits, button:not(.plain).bits {
    color: #fff;
    background: rgb(210,128,15);
    background: linear-gradient(0deg, rgba(210,128,15,1) 0%, rgba(234,156,21,1) 100%);
    border-color: #C08522;
    text-transform: capitalize;
}

.button:not(.plain).bits:hover, button:not(.plain).bits:hover {
    background: linear-gradient(0deg, rgba(210,128,15,1) 0%, rgba(234,156,21,1) 100%);
    background: linear-gradient(0deg, rgba(234,156,21,1) 0%, rgba(210,128,15,1) 100%);
}

.modal .cancel-button {
    color: #fff;
    background: rgb(0, 0, 0);
    background: linear-gradient(0deg, rgb(179, 179, 179) 0%, rgb(206, 206, 206) 100%);
}

.modal:hover .cancel-button:hover {
    background: linear-gradient(0deg, rgb(206, 206, 206) 0%, rgb(179, 179, 179) 100%);
}

.button.tab-button.transparent, button.tab-button.transparent {
    background-color: #fff;
    border-color: #0000;
    color: #ffffff;
}


.crate-types {
    text-transform: uppercase;
    font-weight: normal;
    font-size: 15px;
}

.crate-types .active {
    background-color: #F57D20;
    border-left: 0px;
    color: #fff;
}

.crate-types :hover {
    background-color: #C3CDD8;
    border-left: 0px;
}


/* Tabs */

.tabs .tab {
    color: #000;
    border-radius: 0px;
    background-color: #fff;
    border: 0px solid #C3CDD8;
    border-bottom: 4px solid #C3CDD8;
    font-size: 1.1rem;
    font-weight: 700;
    margin: 3px;
    width: 24.3%;
}

.tabs .tab.active {
    background-color: #fff;
    font-weight: 600;
    border-bottom: 1px solid transparent;
}

.tabs .tab-body {
    border: 0px solid;
}

.tabs .tab.active {
    background-color: #fff;
    font-weight: 700;
    color: #000;
    border-bottom: 4px solid #F57D20;
}


/* input */

.input-group .input-button {
    border-left: 1px solid #C3CDD8;
    color: #fff;
    background-color: #f8f8f8;
}

input, textarea, .input-group.fill, input.rigid, textarea.rigid, .select, select {
    border-radius: 0px;
    border: 1px solid #C3CDD8;
}

.alert {
    border-radius: 0px;
    letter-spacing: 0.5px;
    text-transform: Capitalize;
}

.alert.success {
    background-color: rgba(48,184,71,1);
    background: linear-gradient(0deg, rgba(48,184,71,1) 0%, rgba(59,223,86,1) 100%);
    border: 1px solid #30B747;
}

.alert.warning {
    background: rgb(217,125,0);
    background: linear-gradient(0deg, rgba(217,125,0,1) 0%, rgba(243,154,0,1) 100%);
    border: 1px solid #e38b00;
}

.alert.error {
    background: rgb(222,28,34);
    background: linear-gradient(0deg, rgba(222,28,34,1) 0%, rgba(254,87,70,1) 100%);
    border: 1px solid #DD1A21;
}
}

@-moz-document url("https://www.brick-hill.com/forum/") {
.main-holder {
    padding-top: 30px;
    background-color: #fff;
    padding: 30px;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/currency/") {
.tabs .tab {    
width: 24.3%;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/shop/"), url-prefix("https://www.brick-hill.com/messages") {
.tabs .tab {    
width: 49.3%;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/trades") {
.tabs .tab {    
width: 32.8%;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/trade/create/") {
.tabs .tab {    
width: 48.7%;
}
}
