css:
@-moz-document domain("digitalextremes.zendesk.com") {
div.logo>a>img {
    filter: invert(0.8687);
}

body {
    background-color: rgb(33,33,36);
    color: rgb(204,204,204);
}

body a,
.nav-wrapper a,
.user-info > [role="button"] {
    color: hsl(211, 70%, 70%);
    text-decoration-color: hsl(211, 70%, 70%);
}

.nav-wrapper a:hover, .nav-wrapper a:focus, .nav-wrapper a:active,
.user-info > [role="button"]:hover, .user-info > [role="button"]:focus, .user-info > [role="button"]:active {
    color: hsl(204, 88%, 62%);
}

.breadcrumbs li a:visited,
.striped-list-title:visited {
    color: hsl(210, 80%, 60%);
}


nav.my-activities-nav {
    background-color: rgb(51,51,70);
    color: rgb(204,204,204);
}

.collapsible-nav-list li,
.sidenav-item {
    color: rgb(204,204,204);
}

.collapsible-nav-list li a {
    color: rgb(196, 207, 224);
}

section.collapsible-sidebar {
    background-color: rgb(51,51,70);
}

section.section-articles {
    background: none;
}

section.collapsible-sidebar dt {
    color: #eee;
}

input,
textarea,
.nesty-input {
    background-color: hsl(220, 10%, 20%);
    color: rgb(222,222,222);
}

form.search input,
form.search::before,
form.search input[type="search"] {
    background-color: hsl(0, 0%, 82%);
    color: #1a1a1a;
}

form.search::before {
    background: none;
}
form.search input:focus-within,
form.search input[type="search"]:focus {
    background-color: hsl(0, 0%, 78%);
    color: #000;
}

.blocks-item {
    background-color: hsl(220, 10%, 16%);
    color: rgb(222,222,222);
}

.blocks-item-link {
    color: rgb(222,222,222);
}

.blocks-item:hover, .blocks-item:focus, .blocks-item:active {
    background-color: hsl(220, 10%, 28%);
}

.article-list-item a,
.promoted-articles-item a {
    color: hsl(211, 24%, 78%);
}

.section-tree-title a {
    color: hsl(211, 30%, 82%);
}

.table th, .table th a {
    color: #8f8f91;
}

.dropdown-menu {
    background: hsl(260, 12%, 92%);
    opacity: 0.97;
}
}
