css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("item.taobao.com") {
.header,.flash-banner,.col-sub,.fence,#detail-other,.footer,#J_LiSafety,.gallery,.bonus,#gd,#J_PostageCont{display:none}
#detail .property .wrap{margin:0!important}
#content{
margin-top:100px!important;
}
#J_StrPrice{font-size:48px!important;font-weight:bold!important}
}

@-moz-document domain("buy.taobao.com") {

#logo, .buy-process-nav, .good-pic-box, .J_WangWang, .point-info,  .extra, .point,#footer{display:none}

#address-collection li{display:none}
#address-collection li.selected{display:block}

#checkCode{height:50px;width:150px;display:block;font-size:36px!important;}

.priceText,strong#actualPrice{font-size:28px;font-weight:bold;color:#f50;
}

strong#actualPrice{font-size:36px!important;font-weight:bold!important;
}

}
