additional_info:
! IMPORTANT NOTES !

1. You can change the background by replacing url adress in section "background-image" and use whatever you like.
background-image: url("INSERT LINK HERE") !important;

2. Position of Lance image will probably need to be customized depending on your screen size. You can move it to the right verge of the page by changing the value in the last line of the code - "left".
left: 169px;
For example you can type 220px instead of 169px: left: 220px;
in order to move it further to the right
or left: 85px;
in order to move it more to the left.

css:
@-moz-document domain("eldarya.fr"), domain("eldarya.com"), domain("eldarya.com.br"), domain("eldarya.pl"), domain("eldarya.de"), domain("eldarya.dk"), domain("eldarya.es"), domain("eldarya.hu"), domain("eldarya.it"), domain("eldarya.nl"), domain("eldarya.ro"), domain("eldarya.ru"), domain("eldarya.se"), domain("eldarya.uk")
{

/*daily maana popup - Ash*/

 div.flavr-container.modal.center.first-connexion.npc-undefined.new-layout-popup.shown .flavr-message h1
  {
    display: none;
  }

  div.flavr-container.modal.center.first-connexion.npc-undefined.new-layout-popup.shown .flavr-message:after
  {
    content: ' in exchange for a nice frying pan... Do we have a deal?';
    margin-left: auto;
    margin-right: auto;
    text-align: center;
    font-size: 1.4rem;
    color: #fff;
    line-height: 0.9;
  }

  div.flavr-container.modal.center.first-connexion.npc-undefined.new-layout-popup.shown .flavr-content
  {
   margin-right: -300px;
   margin-top: 95px;
  }

  body.season-s1 .flavr-container.first-connexion .flavr-fixer .flavr-outer
  {
    background: none;
  }

  body.season-s2 .flavr-container.first-connexion .flavr-fixer .flavr-outer
  {
    background: none;
  }

  body.season-s1 .flavr-container.first-connexion .flavr-fixer .flavr-outer:after
  {
    background: url("https://64.media.tumblr.com/425e3343b693239416cf9c2188c76994/tumblr_p4cpk2zCTe1wmapvzo4_500.png");
         background-repeat: no-repeat;
     margin-left: 170px;
  }

  body.season-s2 .flavr-container.first-connexion .flavr-fixer .flavr-outer:after
  {
    background: url("https://64.media.tumblr.com/425e3343b693239416cf9c2188c76994/tumblr_p4cpk2zCTe1wmapvzo4_500.png");
       background-repeat: no-repeat;
      margin-left: 170px;
  }

  div.flavr-container.modal.center.first-connexion.npc-undefined.new-layout-popup.shown .flavr-button
  {
     display: block;
	padding: 0px;
    	border: 0px;
  }

  div.flavr-container.modal.center.first-connexion.npc-undefined.new-layout-popup.shown .flavr-button:after
  {
    content: '*take your daily maana and run*!';
    background: linear-gradient(211deg, #F600FF 0%, #A73FF0 100%);
    box-shadow: inset 0 0 21px rgba(187,9,198,0.55), 0 5px 4px 1px rgba(79,16,128,0.36);
    font-size: 1.2rem;
    padding: 0.2rem 0.6rem;
    display: inline-block;
    font-weight: bold;
    text-align: center;
    border: 2px solid #fff;
    border-radius: 50px;
    color: #fff;
    line-height: 1.8;
    font-family: "Alegreya Sans SC", sans-serif;
    letter-spacing: 1px;
    position: relative;
    cursor: pointer;
    margin-left: -90px;
  }

  .flavr-container .flavr-overlay
  {
   margin: -80px;
  }

  .flavr-container.first-connexion #first-connexion-reward
  {
    padding: 0 50px;
    margin-left: 20px;
    margin-right: 70px;
  }

  .flavr-container.new-layout-popup .flavr-outer::after
  {
    animation: popup-skew alternate infinite 8000ms linear, show-popup forwards 1000ms ease-in-out 950ms;
  }

}

@-moz-document domain("eldarya.fr"), domain("eldarya.com"), domain("eldarya.com.br"), domain("eldarya.pl"), domain("eldarya.de"), domain("eldarya.dk"), domain("eldarya.es"), domain("eldarya.hu"), domain("eldarya.it"), domain("eldarya.nl"), domain("eldarya.ro"), domain("eldarya.ru"), domain("eldarya.se"), domain("eldarya.uk")
{

/*Ash as a companion on pet page and profile*/
  
  .pet-gender.pet-gender-f
  {
    background: url("https://www.eldarya.com/static/img/new-layout/pet/pet-gender-m.png");
  }

  #pet-image-container-inner
  {
    content: url("https://i.imgur.com/RYfvHA3.png");
    animation: popup-skew alternate infinite 7500ms linear, show-popup forwards 600ms ease-in-out 9500ms;
    height: 99%;
    width: 99%;
    position: absolute;
    left: 0%;
    top: 0%;
    transform: translate(-50%, -50%);
    max-width: 100%;
    max-height: 100%;
  }

  #pet-resources .pet-resource-container .pet-resource.food
  {
    background: black;
  }

  #pet-resources  .pet-resource.affection .gauge
  {
    background: #f02f27;
  }

  #pet-resources .pet-resource .gauge::after
  {
    background: none;
  }

  #name-container h3::before
  {
    content: "Lance" !important;
    font-size: 50px !important;
  }

  #name-container h3
  {
    font-size: 0 !important;
  }

  #name-container h4::before
  {
    content: "Dragon"!important;
    font-size: 25px !important;
  }

  #name-container h4
  {
    font-size: 0 !important;
  }

  #pet-infos #luck-text::before
  {
    content: "Who needs luck, when you can have a pan? ";
    font-size: 18px;
  }

  #pet-infos #luck-text
  {
    font-size: 0 !important;
  }

  #pet-infos .evolve-info .cant-evolve
  {
    font-size: 0 !important;
  }

  #pet-resources .pet-resource.energy
  {
    background: darkgrey;
  }

  #player-display-pet
  {
    background: url("https://i.imgur.com/4gQ3omc.png") no-repeat;
    background-size: auto 100%;
    height: 560px !important;
    width: 250px !important;
  }

  #player-display-pet > canvas
  {
    display: none !important;
  }

  #player-display-pet > img
  {
    display: none !important;
  }

  #pet-infos.no-pet::after
  {
    content: "Lance" !important;
    font-size: 60px !important;
    font-weight: bold;
    color: #20084d;
    text-shadow: 0 0 2px white, 0 0 1px white;
    margin-left: -45px;
    margin-top: 50px;
  }

  #infos-container #pet-infos
  {
    font-size: 0px !important;
  }
}
 



@-moz-document domain("eldarya.fr"), domain("eldarya.com"), domain("eldarya.com.br"), domain("eldarya.pl"), domain("eldarya.de"), domain("eldarya.dk"), domain("eldarya.es"), domain("eldarya.hu"), domain("eldarya.it"), domain("eldarya.nl"), domain("eldarya.ro"), domain("eldarya.ru"), domain("eldarya.se"), domain("eldarya.uk")
{

/*background*/
  .page-main-container
  {
    background-image: url("https://i.imgur.com/pmwuf1O.jpg")!important;
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
    max-width: 100%;
  }

  .home-interface-container #left-bg img
  {
    display: none;
  }

  #main-section
  {
    padding-top: 90px;
    padding-left: 30px;
    padding-right: 30px;
  }

  .pet-interface-container .section-title
  {
    color: white;
    opacity: 0.90;
    font-family: "Alegreya Sans SC", sans-serif;
    font-size: 50px;
    line-height: 45px;
    text-transform: uppercase;
    margin: 10px auto;
    text-shadow: 0 0 4px rgba(255, 255, 255, 0.5) var(--main-season-color);
  }


/*top bar*/
  #top-bar h1
  {
    font-size: 28px;
  }

  #top-bar
  {
    position: fixed;
    top: 0;
    left: 0;
    height: var(--topbar-height);
    line-height: var(--topbar-height);
    background-color: #1e8dc8;
    opacity: 0.8;
    width: 100%;
    min-width: 1240px;
    z-index: 8009;
  }



/*menu bar*/
  #menu-bar #menu li a
  {
    font-size: 21px;
    text-shadow: 0 0 8px var(--main-season-color);
    line-height: 38px;
    font-weight: 600;
  }


/*pet page fonts and colors*/
  #name-container h2
  {
    font-size: 30px;
  }

  #name-container h3
  {
    color: #1e8dc8;
    font-size: 60px;
    text-shadow: 0 0 2px white, 0 0 1px white;
  }

  #name-container h4
  {
    color: #1e8dc8;
    text-shadow: 0 0 2px white, 0 0 1px white;
  }


/*pet page Lance - exploration map overlay*/
  .pet-interface-container #right-container:not(:hover)
  {
    content: url("https://i.imgur.com/HRkFDnY.png");
    width: 560px;
    position: relative;
    margin-top: 0px;
    left: 169px;
  }
}
