css:
@-moz-document domain("hotstock.io") {
/*---------------------------
-- Hotstock.io Night Mode  --
-- Author: EleventhEric    --
-- Date: 22NOV20           --
-- Version: 1.0            --
-----------------------------*/

/* Main background */
body {
    background-color: #1E1E1E;
}

.subtitle.is-6point5 {
    color: #FFF;
}

/* newly added background */
.is-two-thirds div.section.is-small.grey-background {
    background-color: #2B2B2B;
}

/* newly added text color for each item */
.is-two-thirds div.section.is-small.grey-background div.level.subtitle.is-5.contains-seeall div.level-item {
    color: #FFF;
}

/* newly added section title */
.is-6point5.has-spacing-above.is-newly-added a {
    color: #FFF;
}


.title.is-6point5 a {
    color: #FFF;
}


.title.is-4.has-text-centered {
    color: #FFF;
}

.has-text-centered {
    color: #FFF;
}

.section.is-small {
    color: #FFF;
}

.is-small.grey-background {
    background-color: #2B2B2B;
}

.title.is-6.no-select {
    color: #fff;
}

.is-simple.is-borderless {
    background-color: #1E1E1E;
}

.title.text-header.has-text-centered-mobile {
    color: #FFF;
}

.table.table-productshopsevents.table-products.is-hoverable.no-select thead {
    background-color: #2B2B2B;
}

.table.table-productshopsevents.table-products.is-hoverable.no-select thead tr th {
    color: #FFF;
}

.table.table-productshopsevents.table-products.is-hoverable.no-select tbody tr {
    background-color: #363636;
}

.text-cell-shopname {
    color: #FFF;
}

.table.table-productshopsevents.table-products.is-hoverable.no-select tbody tr td.is-narrow {
    color: #FFF;
}

.subtitle.is-6.text-cell-productshoplaststockdate {
    color: #FFF;
}

.is-primary.is-secondary.is-primary-small {
    color: #FFF;
}

.section.footer-upper {
    background-color: #1E1E1E;
}

.tagline {
    color: #FFF;
}

/* Settings Page */
.is-medium {
    color: #FFF;
}

.is-small div.columns div.column div.field-group div.field div.control p.read-only {
    color: #FFF;
}

/* alert popover */
.vue-popover.dropdown-position-bottom p.is-size-7 {
    color: #000;
}
}
