.red-ui-tabs ul li a.red-ui-tab-label
{
}

.red-ui-tabs ul li
{
  height: 25px;
}

.scroll-container::-webkit-scrollbar
{
  display: block;
  width: 10em;
  overflow: auto;
  height: 2em;
}

#red-ui-workspace > div.red-ui-tabs.red-ui-tabs-add.red-ui-tabs-menu.red-ui-tabs-scrollable > div.red-ui-tab-button.red-ui-tab-scroll.red-ui-tab-scroll-right > a
{
  display: none;
}

#red-ui-workspace > div.red-ui-tabs.red-ui-tabs-add.red-ui-tabs-menu.red-ui-tabs-scrollable > div.red-ui-tabs-scroll-container
{
}
#red-ui-workspace-tabs-menu
{
left: 0px !important;

}
#red-ui-workspace > div.red-ui-tabs.red-ui-tabs-add.red-ui-tabs-menu.red-ui-tabs-scrollable
{
  position: absolute;
  left: 0;
  width: 170px;
  bottom: 0px;
  top: 0px;
  height: 100%;
  display: grid;
  display: -webkit-inline-box;
  overflow-y: scroll;
  padding-left: 5px;
}

#red-ui-workspace-chart
{
  margin-left: 170px;
}

#red-ui-workspace > div.red-ui-tabs.red-ui-tabs-add.red-ui-tabs-menu.red-ui-tabs-scrollable > div.red-ui-tabs-scroll-container
{
  height: 100%;
  margin-bottom: 60px;
  overflow-x: hidden;
  overflow-y: scroll;
  margin-top: 60px;
}

#red-ui-workspace-tabs
{
  display: contents;
}
