css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("juick.com") {
HTML{background:#FAF6D6 !important;}
A{color:blue !important;}
#hlinks {border-bottom:none !important; height:37px !important; margin:15px 0 0 0 !important; padding:0.5em 200px !important; background:url(http://img805.imageshack.us/img805/7015/bgmatrix940x175.jpg) repeat-x !important;}
#hlinks li{padding:0 1em 0 1em; !important}
#hlinks UL LI A{color:white !important; text-decoration:none !important}
#hlinks UL LI A:hover{color:red !important; text-decoration:underline !important}
#pagetabs li{background:#91D391 !important; border:none !important; -moz-border-radius:5px !important; -khtml-border-radius:5px !important; -webkit-border-radius:5px !important; border-radius:5px !important;}
#lcol{padding:0 7px 15px 7px !important; border:1px solid black !important; background:#F5C367 !important; -moz-border-radius:15px !important; -khtml-border-radius:15px !important; -webkit-border-radius:15px !important; border-radius:15px !important;}
#lcol h2{border-bottom:none !important; margin-bottom:5px !important;}
#lcol *{font-family:'Times New Roman',sans-serif !important;}
#lcol li {margin-bottom:0 !important;}
#lcol * UL LI A:link{color:black !important; text-decoration:none !important}
#lcol * A:hover{color:black !important; text-decoration:underline !important}
#content{font-family:arial,helvetica,sans-serif !important; font-size:10pt !important;  padding:20px !important; background:#91D391 !important; border:none !important; -moz-border-radius:15px !important; -khtml-border-radius:15px !important; -webkit-border-radius:15px !important; border-radius:15px !important;}
#footer{color:green; font:10pt 'Lucida Console',monospace; text-align:left !important; height:45px !important; margin:15px 0 13px 0 !important; padding:0.5em 200px !important; background:#CCCCCC !important;}
}
