css:
@-moz-document domain("7daystodiemods.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #d51e1e;
        }

body.boxed-layout.custom-background, body.boxed-layout {
	background: #222;
}
body.boxed-layout #page {
	background: #000;
}
.cat-links a:hover, body, button, input, select, optgroup, textarea, .widget ul li a {
	color: #b0b0b0;
}
.related-post-meta a, .entry-meta a, .related-post-meta, .entry-meta {
	color: #666;
}
.cat-links {
	color: #333;
}
.bam-topbar {
	background: #050505;
	border-color: #111;
}
.top-navigation a:hover, .comment-metadata a, .pingback .comment-edit-link {
	color: #777;
}
.top-navigation a {
	color: #ddd;
}
.bam-topbar-social.dark .bam-social-link i {
	color: #ccc;
}
input[type="text"], input[type="email"], input[type="url"], input[type="password"], input[type="search"], input[type="number"], input[type="tel"], input[type="range"], input[type="date"], input[type="month"], input[type="week"], input[type="time"], input[type="datetime"], input[type="datetime-local"], input[type="color"], textarea {
	border-color: #222 !important;
	color: #999 !important;
}
input, textarea {
	background: #000;
}
.widget-title {
	color: #fff;
	border-color: #222;
}
.single .entry-title, .post-navigation .nav-previous .post-title, .post-navigation .nav-next .post-title, .related-post-title a, .bam-entry .entry-title a {
	color: #fff;
}
.pagination .page-numbers {
	border-color: #333;
	color: #b0b0b0;
}
.post-navigation .nav-previous .meta-nav, .post-navigation .nav-next .meta-nav {
	color: #4d4d4d;
}
.bam-tags-title {
	background: #fff;
	color: #000;
}
.comment-list article, .comment-list .pingback, .comment-list .trackback, .comment-reply-title {
	border-color: #222;
}
p.comment-note-for-help {
    border-color: #0f0d09;
    background-color: #0f0a04;
}
.comment-author {
	color: #eaeaea;
}
.archive .page-title {
	color: #fff;
	border-color: #222;
}
.wp-block-code {
    color: #eee;
    border-color: #222;
}
pre {
	background: #111;
}
}
