@-moz-document domain("polishdjschart.pl") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #000;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #000;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #e72445
        }

.header-border {
    border-color: #111;
}
.blur-hamburger {
    background: rgba(0, 0, 0, .5);
    box-shadow: inset 0px -5px 30px -20px rgba(0, 0, 0, .75);
}
body {
    background-color: #000;
    color: #fff;
}
.text-black {
    color: #fff !important;
}
[src='https://polishdjschart.pl/wp-content/themes/polishdjschartv2/assets/svg/instagram-dark.svg'], [src='https://polishdjschart.pl/wp-content/themes/polishdjschartv2/assets/svg/facebook-dark.svg'], [src='/wp-content/themes/polishdjschartv2/assets/svg/left-arrow-inactive.svg'], [src='/wp-content/themes/polishdjschartv2/assets/svg/right-arrow.svg'], [src='https://polishdjschart.pl/wp-content/themes/polishdjschartv2/assets/svg/long-right-arrow.svg'], [src='https://polishdjschart.pl/wp-content/themes/polishdjschartv2/assets/svg/bg-vote-poland.svg'] {
	filter: invert(100);
}
.owl-dot {
	color: #666;
}
div.col {
	color: #666 !important;
}
div.menu li > a, ul.menu li > a {
	color: #fff;
}
.bg-light-gray {
	background: #0c0c0c;
}
label.form-label {
	border-color: #666;
}
label.form-label input, label.form-label textarea {
	border-left-color: #666;
}
input, textarea {
	background: #000;
	color: #fff;
}
.checkmark {
	border-color: #f1f1f1;
}
.checkbox-label input:checked~.checkmark {
	background: #000;
}
}
