css:
@-moz-document domain("lafundacionscp.wikidot.com"), domain("scp-es.com") {
div#container-wrap {
    background: url(https://i.ibb.co/QYQ2d4z/body-bgdark.png) top left repeat-x;
}

body {
    background-color: rgb(44, 47, 51);
    font-size: 0.80em;
    color: #fff !important;
}
a {
     color: #fff;
     text-decoration: none;
     background: transparent;
}
 a:visited {
     color: #ababab;
}
#side-bar a:visited {
    color: #ababab;
}

element.style {
    text-align: center;
    color: #fff;
}
h1 {
    color: #ccc;
}
 .content-panel.standalone {
     background: #484848;
}
.content-panel {
    border: solid 1px #888880;
    border-radius: 10px;
    background-color: #999990;
    margin: 10px 0 15px;
    box-shadow: 3px 3px 6px #bbb;
    box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(72,72,72,0.2), inset 0 10px 20px rgb(72 72 72 / 0.25), inset 0 -15px 30px rgb(72 72 72 / 0.1);
}
.yui-navset .yui-content {
    background-color: #484848;
}
.content-panel .panel-body {
    padding: 5px 10px;
    background: #484848 bottom repeat-x;
    /* box-shadow: 1px 2px 3px rgba(0,0,0,.2) inset; */
}
.news-block .news-title {
    font-weight: bold;
    font-size: 110%;
    margin-bottom: 5px;
    color: #c3c3c3;
}

.international-entry:nth-child(even), .alternate:nth-child(even) {
    background-color: rgb(35 35 35);
    border-top: solid 1px #eeeee0;
    border-bottom: solid 1px #eeeee0;
}

.interwiki {
        font-family: Verdana, Arial, Helvetica, sans-serif;
        background: #292f33;
        border: 1px solid #bdbdbd;
        box-shadow: 0 2px 6px rgb(177 177 177);
        width: 16.25em;
    }

element.style {
}
#side-bar .side-block {
    padding: 10px;
    border: 1px solid #ffffff;
    box-shadow: 0 2px 6px rgb(173 173 173);
    background: #fff;
}
#side-bar .heading {
    color: #fff;
    border-bottom: solid 1px #e8e8e8;
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
.side-block{
      background-color: #292f33 !important;
}
#interwiki .interwiki{
    background: #292f33 !important;
}
.content-panel .content-toc {
    float: right;
    padding: 0 20px;
    background-color: #292f33;
    border: solid 1px #ccc;
    border-radius: 10px;
    margin: 20px 0 5px 5px;
    white-space: nowrap;
    box-shadow: inset 1px 2px 6px rgba(0,0,0,.15);
}
.scp-image-block .scp-image-caption {
    background-color: #404040;
} 
blockquote {
    background-color: #2c2f33;
}
.list-pages-box { 
    background:#2c2f33 !important;
}
div[style] {
        color: #fff;
}
.page-rate-widget-box .rate-points {
    background-color: #5d5d5d !important;
    border: solid 1px #5d5d5d !important;
    border-right: 0;
}

.page-rate-widget-box .cancel a {
    background: #5d5d5d !important;
        color: #fff !important;
}
.page-rate-widget-box .cancel {
    background-color: #5d5d5d;
    border: solid 1px #5d5d5d;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    color: #5d5d5d !important;
}
h1, #page-title {
    color: #fff !important;
}
.owindow .button-bar a {
    background-color: #212121 !important;
}
.owindow .title {
    background-color: #212121;
}
table.wiki-content-table th {
    background-color: #333 !important;
}
.page-content{
    color: #fff !important;
    background-color: red !important;
}
.code {
    background-color: #000 !important;
}
.menu-item div[style] {
    background: transparent;
    color: #fff !important;
}
.page-content [style] {
    background: transparent !important;
    color: #fff !important;
}
.forum-thread-box .description-block {
        background-color: #151515;
}
.thread-container .post .head {
        background: linear-gradient(to right, #000000, #2c2f33);
}
div.new-post a {
        background-color: #060606;
}
#toc {
    background-color: #000000;
}
iframe.scpnet-interwiki-frame {
    filter: brightness(93%) grayscale(100%) invert(85%);
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #292f33;
    color: #fff;
    border: solid #292f33;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #000;
    color: #fff;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #000000;
    color: #fff;
    border: solid #000000;
}
.content-panel{
      background: url(https://i.ibb.co/JsvgLLt/bg-marbledark.png) repeat !important;
    
}
div.content {
    background: #000000
}
::-webkit-scrollbar-thumb {
    background: #757575;
}
::-webkit-scrollbar-track {
    background: rgb(0 0 0 );
}
.section table *:first-child {
    color: #6d6d6d;
}
.section table td:first-child, .section table th:first-child {
    color: white;
}
table.wiki-content-table th {
    color: white;
}
.section {
 background: #2c2f33
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #fff;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #fff;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #5a5a5a;
}
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
     background: #575757;
    color: white;
}
#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
background: black
}
#search-top-box-form input[type=submit] {
    background: linear-gradient(to bottom, #474747,#030303,#6c6c6c);
        color: #e8e8e8;
}
#search-top-box input.empty {
    color: #e8e8e8;
}
#search-top-box-input {
    background-color: #1b1b1b;
}}
