css:
@-moz-document url-prefix("https://notion.so/") {
/* Content Body */

.notion-page-content {
  direction: rtl;
    text-align: right;
}
/* Text */
.notranslate {
  /* direction: rtl; */
    text-align: right !important;
}

/* Page Block */
.notion-page-block {
  /* direction: rtl; */
}
.notion-page-block > a > div > div {
    margin-left: auto;
    margin-right: 0 !important;
}

/* Page Props */
.notion-scroller:nth-child(2) {
  direction: ltr;
}
/* Page Header */
.notion-frame .notion-scroller > div {
  direction: rtl;
}
/* Turn into */
.vertical {
    direction: ltr;
}
.notion-scroller div[role="button"] > div > div:nth-child(1) {
/*   margin-right: 0px;
  margin-left: 8px; */
}

.notion-scroller div > div > div > div {
  /* ... */
/*   left: 8px;
  right: 0px !important; */
}

/* Code Block */
.notion-code-block div {
    text-align: left !important;
    direction: ltr;
}
.notion-collection-item {
 direction: rtl;   
}
}
