css:
@-moz-document domain("www.nitrotype.com") {
body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://cdn.discordapp.com/attachments/774107571601932298/780828957457121280/LEGENDARY_RACER.png");
    background: fixed;
  }

  html
  {
    background: fixed url("https://cdn.discordapp.com/attachments/774107571601932298/780828957457121280/LEGENDARY_RACER.png") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 100%;
  }

 .bg--gradient {
    background: fixed;
}

  .btn--primary
  {
    background: #0c0c0c;
    color: #ffffff;
  }

  .theme--pDefault .profile-title
  {
    color: #ff9808;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #ff9c08;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .tc-t {
    color: #ffad08;
  }

  .theme--pDefault .profile-body
  {
    color: #ff7f08;
  }
}

@-moz-document domain("nitrotype.com") {
.theme--pDefault .profile-bg
{
    background: url("https://cdn.discordapp.com/attachments/718573727918456916/780830942671536149/Untitled_design_21.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #ee5f02 100%);
    color: #d3d3d3;
    background-size: 100%
}

.theme--pDefault .profile-bgHead
{
    background: url("https://cdn.discordapp.com/attachments/718573727918456916/780830942671536149/Untitled_design_21.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #ee5f02 100%);
    color: #d3d3d3;
    background-size: 100%
}

.theme--pDefault.is-gold .profile-bg
{
    background: url("https://cdn.discordapp.com/attachments/718573727918456916/780830942671536149/Untitled_design_21.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #ee5f02 100%);
    color: #d3d3d3;
    background-size: 100%
}
.card--a
{
    background-color: #fff0;
    color: #ea9e06;
}

.dash-copy
  {
    color: #fda603;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }
.dash-copyContainer
  {
      background: #ea8103;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #ff9d00;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #fa0;
    color: #fff0;
  }

  .bg--gradient {
    background: fixed;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/718573727918456916/780830942671536149/Untitled_design_21.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ee5f02 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.discordapp.com/attachments/718573727918456916/780830942671536149/Untitled_design_21.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ee5f02 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/718573727918456916/780830942671536149/Untitled_design_21.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ee5f02 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
.btn--primary {
    background-image: url("https://cdn.discordapp.com/attachments/766508792615010344/776907020800098334/cool-galaxy.jpg");
    color: #fff;
    background-size: 100%;
    background-color: #000 !important;
   
}
.btn--secondary {
    background-image: url("https://cdn.discordapp.com/attachments/766508792615010344/776907020800098334/cool-galaxy.jpg");
    color: #fff;
    background-size: 100%;
        background-color: #000 !important;


}
.growl--a {
    background-image: url("https://cdn.discordapp.com/attachments/766508792615010344/776907823681765406/3F006CA900000578-0-image-a-21_1491475782242.jpg");
    border-color: rgba(2, 2, 2, 0.2);
    background-size: 100%;
    color: #fff;
}
}
