css:
/********************************
   Reddit dark theme, good for
   late night redditing.
   by squid    
********************************/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {

a{
    color: #7E949E !important;
}

#ad-frame {
    background-color: #2C2C2C !important;
    opacity: 0.4 !important;
}

#ad-frame:hover{
    opacity: 100 !important;
}

#ad, #mail.nohavemail, #modmail.nohavemail, a img{
    opacity: 0.4;
}

#ad:hover, a:hover img{
    opacity: 100;
}

#adlink{
    background-color: #2C2C2C !important;
}

.arrow.upmod{
    opacity: 0.4;
}

body, div, h3, .morelink, .tabmenu li a{
    background-color: #2C2C2C !important;
    color: #888888 !important;
    font-family: "DejaVu Sans" ,verdana,arial,helvetica,sans-serif !important;

}

blockquote{
    border-color: #6C757D !important;
}

.error {
    color: #FC7765 !important;
}

.expando-button {
    opacity: 0.4;
}

.expando-button:hover{
    opacity: 100;
}

.fancy-toggle-button a{
    color: #333 !important;
}


fieldset.liveComment legend,  fieldset.liveComment{
    background-color: #2C2C2C !important;
    color: #888 !important;
    border-color: #6C757D !important;
}

.flat-list a{
    background-color: #2C2C2C !important;
    border: none !important;
}

.flat-list a:hover{
    color: #7E949E !important;
}

.flat-vert.title{
    color: #777777 !important;
}

.formtabs-content {
    border-top:4px solid #6C757D !important;

}

input, textarea{
    color: #B7B7B7 !important;
    background-color: #2C2C2C !important;
}

input.btn, .save-button button{
    color: #2C2C2C !important;
}

#header{
    border-color: #6C757D !important;
}

ul.flat-list a{
    color: #888888 !important;
}

.help a{
    color: #808080 !important;
}

.infobar{
    border-color: #EBCEA0 !important
}

.link .score.likes {
    color: #805241 !important;
}

.sidebox.submit, .create{
    background: none !important;
}

sidebox.submit a{
    color: #B9BCBD !important;
}

.morelink {
    background: none !important;
    border: none !important;
    color: #B9BCBD !important;
}

.morelink:hover {
    color: #7E949E !important;

}

.nextprev a, .separator{
    background-color: #2C2C2C !important;
    border-color:#6C757D !important;
}

.nextprev img{
    opacity: 0.4;
}

.nextprev img:hover{
    opacity: 100;
}

#sr-header-area {
    background-color: #2C2C2C !important;
    color: #BDBDBD !important;
}

#sr-more-link {
    background-color: #2C2C2C !important;
    color: #BDBDBD !important;
}

#sr-header-area a {
    color: #BDBDBD !important;
}

.sr-toggle-button, img {
    opacity: 0.4;
}

.sr-toggle-button:hover {
    opacity: 100;
}

.tabmenu li.selected a {
    background-color:#2C2C2C !important;
    border-color:#6C757D #6C757D #2C2C2C !important;
    color: #805241 !important;
}

.tabmenu li a{
     color: #B9BCBD !important;
}

.tabmenu.formtab a {
    border-color:#6C757D #6C757D -moz-use-text-color !important;
}

.tagline a.author, .tagline a.subreddit{
    /*color: #718FAC #566E78 !important;*/
    color: #566E78 !important;
}

.title, .thing a {
    /* color: #3D6288 !important; submission color */
    color: #7E949E !important;
}

.domain a{
    color: #888888 !important;
}

.usertext-body, .md{
    color: #9E9E9E !important; /* comment color */
}

.link .usertext .md {
    border-color: #6C757D !important;
}

.expand{
       color: #444 !important;
}

.trophy-area .content{
    background-color: #2C2C2C !important;
}

.trophy-name {
    color: #9E9E9E !important;
}


a.title.loggedin.click:visited, a.title.click:visited, .title:visited, .title.loggedin:visited {
    color: #444 !important;
}


}
