css:
@-moz-document domain("truyenfull.vn") {
  .dark-theme .navbar-default, .dark-theme.navbar-default {
      border-bottom-color: #2B2B2B !important;
      /* box-shadow: 0 0 1px rgba(10,10,10,0.4); */
      background: #2B2B2B !important;
  }
  .dark-theme .navbar-breadcrumb {
    background: #2E3740 !important;
    border-top: 0;
     display: none;
 }
  .dark-theme .btn-success {
    color: #545454 !important;
    border-color: #2B2B2B !important;
}
  .dark-theme .chapter .truyen-title {
    color: #8e8e8e !important;
    filter: dropshadow(color=#000000, offx=0, offy=0);
}
  .dark-theme .footer .tag-list a, .dark-theme .footer .tag-list a:hover, .dark-theme #truyen-slide .caption>small, .dark-theme .search-holder>.input-group-btn>.btn {
    color: #888888 !important;
    border-color: #505050 !important;
    text-shadow: 1px 1px 0 rgba(10,10,10,0.4);
    background: rgba(10,10,10,0.15);
}
  #body_chapter.background-232323 {
    background-image: none !important;
    background-color: #2B2B2B !important;
    background-repeat: repeat;
}
.dark-theme .footer {
    background-color: #121212;
    border-top: 1px solid #000;
    color: #8D8D8D;
    display: none !important;
}
  .ads-responsive {
    display: none !important;
}
  ::-webkit-scrollbar {
  width: 10px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #2B2B2B; 
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #4D4D4D; 
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #5C5C5C; 
}

element.style {
}
#chapter_error, #chapter_comment {
    display: inline-block;
    margin: 10px auto;
}
.dark-theme .btn-warning:hover, .dark-theme .btn-warning:focus, .dark-theme .btn-warning:active {
    color: #c7c7c7;
    border-color: #000000 !important;
}
.dark-theme .btn-info:hover, .dark-theme .btn-info:focus, .dark-theme .btn-info:active {
    color: #424242;
    border-color: #000000 !important;
}
.dark-theme .box-notice {
    border: 1px dashed #545D62 !important;
    color: #A9A9A9 !important;
    display: none !important;
}
}
