css:
@-moz-document url-prefix("https://runestone.academy/runestone/books/published/fopp"), domain("fopp.umsi.education") {
/* Background */
body, div.container[id="main-content"], footer div.container{ background-color: #202020 !important;}


/* Text color */
div.container[id="main-content"], footer{ color: #fff !important}
}

@-moz-document regexp("https://fopp.umsi.education/assignments/.+") {
/* Assignments */
div.container{ 
    background-color: transparent !important;
    color: #fff !important;
}
}
