css:
@-moz-document url-prefix("https://baltnews.lt/") {
:root {
    --bg: #111;
    --wnd:#262626;
    --wnd2:#353535;
    --vd: #1a1e24;
    --hvr: #444a4f;

    --hdr: #f0f0f0;
    --fnt: #e0e0e0;
    --fntDark: #7E8082;
    --red: #f66;
    --redSoft: #DB6364;
    --blueSoft: #a2c0e0;
    --greenSoft: #95b581;
}

/* Main */

body {
    color: var(--fnt);
    background: var(--bg)
}

.header {
    background: var(--bg);
}

.header__logo {
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
    height: auto;
}

.header__logo img {
    opacity: 0;
}

.navigator-main__list {
    background: var(--bg);
}

.media-heading__content, .container_hotdog {
    background: var(--bg);
}

.media-heading_article {
    background: var(--bg)
}

/* Pop-Up */

.hamburger-menu__container {
    background: var(--wnd);
}

.social-links {
    filter: invert(100)
}

.header__button_menu:after {
    filter: invert(90%)
}

/* Article */

.article-inject_article .article-inject__title {
    color: var(--hdr)
}

.article-media__image {
    border-radius: 5px
}

.to-top {
    background-color: var(--wnd);
}

.layout__wrapper {
    background-color: var(--bg)
}

.media-heading__article-title {
    color: var(--hdr)
}

.media-heading__article-announce a {
    color: var(--fnt)
}

/* Footer */

.footer {
    background: var(--bg)
}

.footer .highlight {
    color: var(--fnt)
}

/* Search */

.search-form__advanced-dropdown {
    color: var(--hdr)
}

.search-form__advanced-dropdown-arrow, .select2-container--baltnews .select2-selection--single .select2-selection__arrow {
    border-color: #fff transparent transparent transparent;
}

.search-form__query button:before {
    filter: invert(100)
}

.search-form__query input, .search-form__query input[type="search"] {
    color: var(--hdr)
}
}
