css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("live.com") {

/*
By Mojtaba sotoudeh
http://baghbanbashi.Net
*/


body {font:normal 9pt tahoma; !important;
text-align: right;
}

.ProductName {font:normal 9pt tahoma; !important;}
#CustComm_120x60 { display:none !important; }
.Middle {top: 54px !important;}

#MainContent {
right: 0px !important;
margin-right: 0px !important;
}


.BorderNoTop {
border-top-width: 0px;
border-right-width-value: 1px;
border-bottom-width: 1px;
border-left-width-value: 1px;
border-left-width-ltr-source: physical;
border-left-width-rtl-source: physical;
border-right-width-ltr-source: physical;
border-right-width-rtl-source: physical;
border-top-style: solid;
border-right-style-value: solid;
border-bottom-style: solid;
border-left-style-value: solid;
border-left-style-ltr-source: physical;
border-left-style-rtl-source: physical;
border-right-style-ltr-source: physical;
border-right-style-rtl-source: physical;
border-top-color: #cccccc;
border-right-color-value: #cccccc;
border-bottom-color: #cccccc;
border-left-color-value: #cccccc;
border-left-color-ltr-source: physical;
border-left-color-rtl-source: physical;
border-right-color-ltr-source: physical;
border-right-color-rtl-source: physical;
font:normal 9pt tahoma;
}
.ReadMsgContainer {
padding-top: 0px;
padding-right: 12px;
padding-bottom: 0px;
padding-left: 12px;
background-color: #ffffff;
text-align: right;
direction: rtl;
font:normal 9pt tahoma;
}
.ReadMsgHeader {
padding-bottom: 5px;
height: auto;
text-align: left;
font:normal 9pt tahoma;
direction: ltr;
}


.InboxContentItemUnread *, .InboxContentItemUnread {
font-weight:bold;
font:bold 9pt tahoma;
text-align: right;
direction: rtl;
}

.PlainTextMessageBody pre {
white-space: normal;
font:normal 9pt tahoma;
}
li {
font:normal 9pt tahoma;
}
p {
font:bold 10pt tahoma;
}
.App.BottomUnmanaged .ContentLeft {
    left: 0;
    position: absolute;
    text-align: left;
    top: 0;
}
hig.css (line 1)
.ContentLeft {
    margin-left: 5px;
    margin-right: 5px;
    overflow: hidden;
    text-align: left;
    width: 150px;
}
.sb.sb_msi .sb_tc, .sb.sb_msi_vc .sb_tc {
    min-height: 10px;
    padding-left: 48px;
    text-align: left;
}
.sb {
    background: none repeat scroll 0 0 #F1F7F7;
    border: 1px solid #CCCCCC;
    position: relative;
    text-align: left;
}
.MsgPartGradient {
    border: 0 none !important;
    direction: ltr;
    overflow: hidden;
    padding: 4px 12px 10px 9px;
    text-align: left;
}


/* ---------------------HEADER---------------------------*/

/*hide upper menu space*/
#PageBottomElt{
    display:none !important;
}
.Middle {
    top:7px !important;
}

/*move the menu down*/
#HeaderContainer *, #m_wh *{
background:transparent !important;
}

#c_header{
position: absolute !important;
bottom:0 !important;
height:2em !important;
}

/*hide everything than 'signout'*/
#c_header .c_hleft, #c_me .c_melnkspan, #c_profile, #c_wimm, #c_header ul .c_m t_hovl, #c_header .c_melnkspan{
display:none !important;
}

/*signout text*/
#c_signout{
position: fixed !important;
left:0.2em !important;
bottom:0.1em !important;
font-size:1.5em !important;
color:black !important;
font-weight:bold !important;
}

#c_signout:hover{
color:red !important;
}


/* ------------------------------------------------*/

/*hide bing logo*/
#c_search_psb_f{
    background-image:none !important;
}

/*collapse to the right(where the ads were)*/
#MainContent {
     margin:auto !important;
     right:0.5em !important;
}

/*hide left menu options: quickview and msn */
#quickViewList, #webimContactList{
      display:none !important;
}

/*hide ads*/
#SkyscraperContent, .Crm120Container, #RadAdSkyscraper, .Ad300x250, .c_ads_ad{
      display:none !important;
}

/*clean up some mess from the left-bottom in calendar mode*/
#c_content iframe{
      display:none !important;
}

/* ---------------------FOOTER---------------------------*/

/*hide footer*/
#FooterContainer, #m_wf, #uxp_ftr_control, #leftPanePrivacy {
      display:none !important;
}
}
