css:
@-moz-document url(https://television.telerama.fr/tele/grille?maintenant) {

body {
    background-color: #c5c6c4;
}
.tv-grid--header.desktop {
    width: 1100px;
    margin-right: auto;
    margin-left: auto;
}
.tv-grid--header_gridchoice {
    background-color: #FFF;
}
.page--tv-grid .tv-grid--header_gridchoice {
    padding: 5px 0 4px;
    width: 368px;
    float: left;
}
.page--tv-grid .tv-grid--header_filters {
    padding: 5px;
    background-color: #fff;
    float: right;
    width: 612px;
}
.page--tv-grid .tv-grid--header_filters-left > li {
    margin-right: 0px;
}
.page--tv-grid .tv-grid--header_filters-left > li.tonight.active {
    margin-right: 1px;
}
.page--tv-grid .tv-grid--header_filters-left li div#choose_hour_chosen {
    margin-top: -4px;
    width: 140px !important;
}
.page--tv-grid .tv-grid--header .chosen-single {
    padding-left: 5px;
}
.page--tv-grid .tv-grid--header_filters-right > li {
    margin-right: -2px;
    margin-top: 3px;
}
.page--tv-grid .tv-grid--header_filters-right > li + li {
    margin-left: 7px;
}
.page--tv-grid .tv-grid--header_filters-right li div#choose_genre_chosen {
    margin-top: -4px;
    width: 150px !important;
}
.page--tv-grid .tv-grid--header_filters-right label {
    margin-left: 4px;
    font-size: 13px;
}
.page--tv-grid .tv-grid--header_dates {
    background-color: #f2f2f2;
    box-shadow: 0px 10px 10px #6b6b6b;
    width: 1100px;
}
.page--tv-grid .tv-grid--headers_hours-container {
    background-color: #5288b6;
    color: white;
    font-size: 14px;
    position: relative;
    margin: 0;
    width: 1100px;
}
    
.page--tv-grid .tv-grid--marker {
    width: 2px;
}
.page--tv-grid .tv-grid--header .chosen-container {
    padding: 2px 0;
    margin-right: 4px;
    width: 130px !important;
}
.page--tv-grid .create-grid {
    padding: 4px 4px 5px 4px;
    float: left;
}
.page--tv-grid .tv-grid--header_filters-left > li > a {
    padding: 4px;
}
.page--tv-grid .tv-grid--header_dates li {
    text-transform: initial;
    padding: 5px 0;
}
#tv-grid--body {
    border: 0px solid gray !important;
    box-shadow: 0px 0px 10px #6b6b6b;
    width: 1100px;
}
.page--tv-grid .channel--program {
    border-right: 2px solid rgba(255, 255, 255, 0.8);
    position: relative;
}
.page--tv-grid .channel--program > a {
    border: 0px solid #ddd !important;
    padding: 0px 5px 0 5px !important;
    background-color: rgba(188, 188, 188, 0.14);
}
.page--tv-grid .channel--program > a:hover {
    background-color: rgba(188, 188, 188, 0.4);
}
.page--tv-grid .channel--program.short {
    line-height: normal;
}
.page--tv-grid .channel--logo {
    text-align: center;
    line-height: 65px;
    background-color: #ddd;
    border-right: 2px solid rgba(255, 255, 255, 0.8);
}
.page--tv-grid .tv-grid--channel li {
    height: 65px !important;
}
.page--tv-grid .channel--logo img {
    vertical-align: middle;
    max-width: 50px;
    width: 50px;
}
.page--tv-grid .tv-grid--hour {
    font-size: 13px;
    color: #777777;
    background-color: rgba(255, 255, 255, 0.8);
    display: table;
    padding: 2px 4px 0px 4px;
    letter-spacing: 1px;
    font-weight: bold;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}
.page--tv-grid .tv-grid--title {
    margin: 0px 0 8px 2px;
    font-size: 17px !important;
    font-weight:bold !important;
}
.page--tv-grid .tv-grid--genre {
    font-size: 13px;
    display: table;
    padding: 2px 4px 0px 4px;
    background-color: rgba(255, 255, 255, 0.8);
    letter-spacing: 1px;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    margin-top: -1px;
    min-width: 32px;
}
.page--tv-grid .tv-grid--genre .icon-trailer {
    font-size: 14px;
    margin-top: -4px;
    margin-left: 1px;
}
.page--tv-grid .tv-grid--image {
    margin-top: 0px;
    margin-left: -5px;
    margin-right: 5px;
}
.page--tv-grid .tv-grid--image img {
    opacity: 0.7;
}

.page--tv-grid .tv-grid--channel {
    border-bottom: 2px solid rgba(255, 255, 255, 0.8);
    background-color: #FFF;
}
.page--tv-grid .tv-grid--channel:nth-child(3) {
    border-top: 6px solid rgba(109, 105, 105, .8);    
}
/* ajoute des lignes pour séparer les différentes genres de chaînes */
/* .page--tv-grid .tv-grid--channel:nth-child(23), .page--tv-grid .tv-grid--channel:nth-child(27), .page--tv-grid .tv-grid--channel:nth-child(39), .page--tv-grid .tv-grid--channel:nth-child(45) {
    border-bottom: 6px solid rgba(109, 105, 105, .8);
} */

i.tra-4,
i.tra-3,
i.tra-5,
i.tra-1,
i.tra-2 {
    margin-top: -4px;
}
li[data-genre=''] {
    color: #888888;
}
li[data-genre='1'] {
    background: linear-gradient(90deg, rgba(247,187,187,1) 0%, rgba(247,187,187,1) 50%, rgba(255,255,255,1) 100%); 
}
li[data-genre='2'] {
    background: linear-gradient(90deg, rgba(247,244,230,1) 0%, rgba(247,244,230,1) 50%, rgba(255,255,255,1) 100%); 
    color: #b5a578;
}
li[data-genre='3'] {
    background: linear-gradient(90deg, rgba(219,232,247,1) 0%, rgba(219,232,247,1) 50%, rgba(255,255,255,1) 100%);
    color: #678bad;
}
li[data-genre='4'] {
    color:#73a3b1;
    background: linear-gradient(90deg, rgba(165,246,247,1) 0%, rgba(165,246,247,1) 50%, rgba(255,255,255,1) 100%); 
}
li[data-genre='5'] {
    background: linear-gradient(90deg, rgba(247,234,213,1) 0%, rgba(247,234,213,1) 50%, rgba(255,255,255,1) 100%); 
    color: #b89066;
}
li[data-genre='6'] {
    background: linear-gradient(90deg, rgba(183,181,243,1) 0%, rgba(183,181,243,1) 50%, rgba(255,255,255,1) 100%); 
}
li[data-genre='7'] {
    background: linear-gradient(90deg, rgba(227,245,239,1) 0%, rgba(227,245,239,1) 50%, rgba(255,255,255,1) 100%);
    color: #57af70;
}
li[data-genre='8'] {
    background: linear-gradient(90deg, rgba(24,22,245,1) 0%, rgba(24,22,245,1) 50%, rgba(255,255,255,1) 100%); 
    color: #60ddf3;
}
li[data-genre='9'] {
    background: linear-gradient(90deg, rgba(243,227,245,1) 0%, rgba(243,227,245,1) 50%, rgba(255,255,255,1) 100%); 
    color: #98849c;
}

li[data-genre='2'] a span.tv-grid--genre {
    color: #9c8954;
}
li[data-genre='3'] a span.tv-grid--genre {
    color: #2c5d8b;
}
li[data-genre='5'] a span.tv-grid--genre {
    color: #b5834f;
}
li[data-genre='7'] a span.tv-grid--genre {
    color: #2f8b49;
}

[data-duration]::after {
    content: attr(data-duration) "'";
    font-weight: bold;
    float: left;
    margin-top: -63px;
    margin-left: 95px;
    font-size: 13px;
    color: rgba(243, 108, 53, 1);
    background-color: rgba(255, 255, 255, .8);
    padding: 0px 4px 0px 4px;
    border-radius: 10px;
}

.page--tv-grid #tv-grid--headers_hours-sticky-wrapper.is-sticky .tv-grid--headers_hours {
    background-color: transparent;
}

@media screen and (min-width: 1024px) and (min-width: 1024px) and (max-width: 1199px) {
    .page--tv-grid .tv-grid--headers_hours-container {
        width: 825px;
        margin-left: auto;
        margin-right: auto;
    }
}
@media screen and (min-width: 680px) and (max-width: 1023px) {
  .page--tv-grid .tv-grid--headers_hours-container {
        margin-left: 0;
        margin-right: 0;
        width: 952px
    }
	#tv-grid--body {
    border: 0px solid gray !important;
    box-shadow: 0px 0px 10px #6b6b6b;
    width: 952px;
	}
	.page--tv-grid .tv-grid--header_dates {
    background-color: #f2f2f2;
    box-shadow: 0px 10px 10px #6b6b6b;
    width: 952px;
	}
	.tv-grid--header.desktop {
    width: 952px;
    margin-right: auto;
    margin-left: auto;
	}
}
}
