css:
@-moz-document domain("ent.iledefrance.fr") {
/* General backgrounds */
html, body, div.material.main.wrapper, body .material section.main{ background-color: #111 !important;}
.notification, popover popover-content, .more, article, textarea{ background-color: #202020 !important;}
textarea, input{ color: #fff !important;}


/* Login page */
.absolute{ background-color: #111 !important;}
.panel.login .welcome-text+form, .panel.activation .welcome-text+form, .panel.login .welcome-text, .panel.activation .welcome-text{ background-color: #202020 !important;}


/* Timeline widgets */

/* General */
div.twelve.cell.ng-scope[ng-include="widget.path"]{ background-color: #202020 !important;}

/* Logbook */
.logbook-widget .ticket-card{ background-color: transparent !important;}
.logbook-widget .ticket-card section h5 span{ color: #4bafd5 !important;}
.logbook-widget .ticket-card section p, .logbook-widget .ticket-card section em{ color: #fff !important;}

/* News */
div.widget.news.ng-scope{ background-color: #202020 !important;}


/* Zimbra Messaging */
article div.flex-all-remains.horizontal-margin.cell-ellipsis{ color: #fff !important;}
.text-title, section.main-col article.vertical-spacing .escape_cke, .escape_cke .ng-scope .ng-scope div{ color: #fff !important;}


/* My Apps */
.bookmarked-apps, .icons-view.apps{ background-color: #202020 !important;}
.icons-view.apps .element legend a{ color: #fff !important;}

/* Mediacentre */
body.mediacentre-v2 h2, body.mediacentre-v2 .textbooks-empty{ color: #fff !important;}
body.mediacentre-v2 resource-card, body.mediacentre-v2 resource-card .flex .complete-card{ background-color: #202020 !important;}


/* Account */
.tabs.heading-tabs{ background-color: #202020 !important;}
span.cell-ellipsis.small-text.block.left-text.ng-binding{ color: #fff !important;}
.multiline-ellipsis-three:after{ background-color: transparent !important;}
}
