css:
@-moz-document domain("pixelanarchy.online") {
* {
    color: #d5ccc7;
    scrollbar-color: #d5ccc7 #153030;
}

.close {
   color: #d5ccc7;
}

.close:hover {
   color: #E5393A;
}

input, .slider, button {
    background-color: #153030;
    border: none;
    border-radius: 5px;
}

input:not(.slider) {
    padding: 5px 10px;
}


.slider::-moz-range-thumb {
    border: none;
    background-color: #d5ccc7;
}

.btnbelow {
    border-color: #d5ccc7;
}

.sidenav {
    background-color: #091515;
    background-image: none;
}

#coords, #users {
    background-color: #091515 !important;
    color:#d5ccc7 !important;
}

#freecanvas, #pixelworldwar, #b2h-cf-popup, .se-pre-con {
    background-color: #091515ee !important;
    color:#d5ccc7 !important;
}

.chat {
    background-color: #091515dd;
}

.chat:hover {
    background-color: #091515ff;
}

::-webkit-scrollbar-track {
  background:#153030;
}

::-webkit-scrollbar-thumb {
  background: #d5ccc7;
}


.modal-content {
    background-color: #091515dd;
    transition: all 0.3s ease 0s;
}

.modal-content:hover {
    background-color: #091515ff;
}

.notiflix-report-content, .notiflix-report-title, .notiflix-report-message {
    color: #d5ccc7 !important;
    background-color: #091515ff !important;
}
}
