/* ==UserStyle==
@name           Nitro Type Black and Blue Theme
@namespace      USO Archive
@author         Justin Xu
@description    `A sleek Black and Blue Theme for Nitro Type; made by Sapphire.`
@version        20210602.2.17
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("www.nitrotype.com") {
    
    /* Gradients */
    
    .theme--pDefault.is-gold, .profile-fg, .profile-bg, .mysteryBox-copy, .btn--primary, .profile-goldBorder, .profile-interior, section.card--a, .profile-bgHead, .mysteryBox-btn, .btn--thinner, .modal-container, .card, .seasonInspector-carPlatformFloor, .seasonInspector-carElevator, .achievement.is-complete, .dais-badge--owned, .table-cell .btn--negative, .btn--thin, .upgrade-tierContainer, .upgrade-quote, .hp-heroBG, .raceResults, .raceResults-rewards, .profile--interior {
        background: linear-gradient(140deg, rgb(0, 0, 0), #000000, #000, #0015cf, #0015cf)!important;
        border: none!important;
        transition: none!important;
        border-radius: 15px;
        box-shadow: none!important;
        color: white!important;
    } /* Signature Sapphire Gradient */
    .btn--positive {
        background: linear-gradient(140deg, #000, #000, #000, #006d0a, #006d0a)!important;
    } /* Black and Green Gradient */
    .btn--negative, .table-cell .btn--negative {
        background: linear-gradient(140deg, #000, #000, #000, #6d0000, #6d0000)!important;
    } /* Black and Red Gradient */
    
    /* Backgrounds */

    .seasonInspector-carPlatformFloorPattern, .seasonInspector-carContainer, .btn:not(.is-loading):before {
        background: none!important;
    } /* No Background */
    .garage-spot:before, .card-cap, .dais .dais-bg, .hp-heroBG:after, .pane-footer, .pane-interior, .raceResults-title--main, .raceResults-title:after, .gridTable-row:not(:first-child) .gridTable-cell, button[class="btn btn--negative btn--outline btn--thin btn--fw"], .stickers-invCell, .table-row, .table-head .table-cell, .tab, article.card, .dropdown, .growl, nav, .btn--tertiary, .footer-nav, .footer-copyright, .footer-copyright .list .tsxs, .link--nav, .dropdown-items, .achievement, .btn--slim, .btn--thick, .has-btn--vertical .btn--fw {
        background: #000!important;
    } /* Black Background */
    .table--teamOverview .table-body .table-row:hover:nth-child(odd) {
        background: blue!important;
    } /* Blue Background */
    .garage-spot.is-active {
        background: rgba(0, 0, 0, 0.75)!important;
    } /* Transparent Black Background */
    .seasonReward-tag--bonus, .teacher-feature, .raceResults-title--place:after, .btn--secondary:focus, .btn--secondary:hover, pre {
        background: white!important;
    } /* White Background */
    .profile-progress .prog .prog-bar .prog-barFill, .dropdown-link:hover, .season-progress-widget--level-progress-bar {
        background: ivory!important;
    } /* Ivory Background */
    .seasonInspector-copy .mbxs, .is-complete .seasonReward-bottom .seasonReward-progress, .is-complete .seasonReward-bottom .seasonReward-tier, .prog .prog-bar .prog-barFill, .modal-close, .modal-close:hover, .seasonReward-progress .prog .prog-points, .seasonReward-tag--free, .seasonReward-status--claimed, .notifications-headerTitle, .nmDash-message, .raceResults-close, .notify:before, .notify, .growl-close {
        background: rgb(35, 0, 207)!important;
    } /* Indigo Background */
    .achievement.is-claimable .achievement-prize {
        background: rgba(255, 255, 255, 0.1);
    } /* Transparent White Background */
    .profile-bgHead, .profile-customization-btn, .profile-customization-btn:hover, .garage, .card--grit, #modal-seasonLearnMore, .modal-seasonBG, .modal-achBox, .motds, .upgrade-quoteAuthor, .raceResults-header, .raceResults-footer {
        background: rgba(0, 0, 0, 0)!important;
        box-shadow: none!important;
        border: none!important;
    } /* Transparent Background */

    /* Text Colors */
    .icon-logout, .tc-t, .is-claimable .achievement-prize .achievement-prizeTitle, .teacher-feature, .btn--secondary:focus, .btn--secondary:hover {
        color: black!important;
    } /* Black Color */
    .profile-title, .profile-invCount, .tsxxs, .profile-levelNum, .profile-levelTitle, .profile-customization-btn, .mysteryBox-title, .focus-effect-container, .tc-t, .tc-lemon, .tc-ts, .nav-link:hover, .dais-copy, .h5, .achievement-prizeTitle, .js-toggleMotds, .pane-userStatTag, .btn.btn--outline:not(.is-active):not(.is-disabled):not(:disabled):not(.is-success):not(.is-failure):not(:hover).btn--secondary, .card--a, .dropdown, .growl, nav, .btn--tertiary, .footer-nav, .footer-copyright, .footer-copyright .list .tsxs, .link--nav, .dropdown-items, .achievement, .nav-list-item.is-current .nav-link, .seasonInspector-copy .mbxs, .is-complete .seasonReward-bottom .seasonReward-progress, .is-complete .seasonReward-bottom .seasonReward-tier, .prog .prog-bar .prog-barFill, .seasonReward-progress .prog .prog-points, .seasonReward-tag--free, .seasonReward-status--claimed, .notifications-headerTitle, .nmDash-message, .profile-customization-btn:hover, .raceResults-close, .btn--slim, .btn--thick, .has-btn--vertical .btn--fw, .notify, .notify:before, .modal-close, .modal-close:hover.growl-close, .growl-close:hover, .achievement.is-claimable .achievement-prize .achievement-prizeTitle {
        color: white!important;
    } /* White Color */
    .profile-progress .prog .prog-bar .prog-barFill, .dropdown-link:hover {
        color: blue!important;
    } /* Blue Color */
    .link--emerald, .dropdown-accountIcon, .icon-help, .icon-eye, .icon-team, .icon-chart, .icon-gear, .raceResults-title--place, .icon-racer, .icon-smiley-l, .icon-stars-l, .icon-check-circle-m, .icon-chat, .is-self:before {
        color: rgb(0, 21, 207)!important;
    } /* Indigo Color */
    .nav-link {
        color: #ffffffbf!important;
    } /* Transparent White Color */

    /* Text Decorations */
    .db, .profile-progress .prog .prog-bar .prog-barFill, .dropdown-link:hover, .has-btn .is-active {
        font-weight: bold!important;
    } /* Boldness */

    /* Hue Rotations */
    .hypeBar {
        filter: hue-rotate(230deg);
    } /* 230 degrees */
    .profile-goldLogo, .icon-nt-gold-1 {
        filter: hue-rotate(183deg);
    } /* 138 degrees */
    canvas[width="258"] {
        filter: hue-rotate(172deg) brightness(90%);
    } /* 172 degrees/90% brightness */
    img[alt="Teaching.com Logo"] {
        filter: hue-rotate(185deg) brightness(90%);
    } /* 185 degrees/90% brightness */
    .notifications-headerTitle:before, .notifications-headerTitle:after {
        filter: hue-rotate(233deg);
    } /* 233 degrees */
    .achievement.is-claimable .achievement-badge:after, .achievement.is-claimable .achievement-badge:before {
        filter: hue-rotate(40deg);
    } /* 40 degrees */
    .upgrade-cardDeco, .upgrade-tier--savings {
        filter: hue-rotate(255deg);
    } /* 255 degrees */

    /* Displays */
    .mysteryBox-titleLeft, .mysteryBox-titleRight, .mysteryBox-titleMore {
        font-size: 26px!important;
        display: inline!important;
    }
    .profile-goldLogo {
        display: none!important;
    }

    /* Misc. Backgrounds */
    .card--a {
        background: radial-gradient(ellipse at 100% 400px, #2300cf, #000, #000)!important;
        box-shadow: 0px 0px 15px black;
    }
    .btn--primary:hover, path[fill="#D62F3A"], a.btn--secondary {
        background: linear-gradient(45deg, rgb(0, 0, 0), #000000, #0015cf, #0015cf)!important;
        fill: rgb(0, 0, 0);
        transition-delay: 0.5s!important;
        box-shadow: 0px 0px 15px blue!important;
    }
    .seasonCallout, .mysteryBox-copy, .goldTeaser {
        background: linear-gradient(300deg, rgb(0, 0, 0), rgb(0, 0, 0), rgba(0, 21, 207, 1), rgba(0, 21, 207, 0.9))!important;
        box-shadow: none!important;
        border-radius: 15px!important;
    }
    html, body {
        background: linear-gradient(300deg, #000, #0000bc, #000) fixed no-repeat;
    }

    .nav-list-item.is-current .nav-link {
        background: radial-gradient(ellipse at bottom, #2300cf, #2300cf, #000, #000)!important;
        border: 1px rgba(255, 255, 255, 0.8) solid;
        border-radius: 5px;
    }
    .nav-link:hover {
        background: radial-gradient(ellipse at 50% 150%, #2300cf, #2300cf, #000, #000)!important;
    }
    .achievement.is-complete, .dais.is-owned, .dais.is-owned:hover, .has-btn .is-active {
        background: linear-gradient(140deg, rgb(0, 0, 0), #000000, #000, #0015cf, #0015cf)!important;
    }
    .lootbox-loader {
        background: url("https://www.nitrotype.com/dist/site/images/mystery-box/mystery-box-qs.png") 50% repeat, #2300cf!important;
    }
    .lootbox-platform {
        background: radial-gradient(#91e1ff 25%,rgb(35, 0, 207) 60%)!important;
    }
    .lootbox-glow {
        background: radial-gradient(rgb(35, 0, 207) 0,rgba(89,255,160,0) 70%)!important;
    }

    /* Margins and Positions */
    .news-teaserCopy a.btn--primary, .news-teaserCopy p.news-teaser {
        margin-bottom: 20px;
    }
    .news-teaserCopy a.btn--primary {
        margin-bottom: 30px;
    }
    section.card--a.is-gold {
        padding: 0px!important;
    }
    .theme--pDefault.is-gold {
        padding-top: 5px;
        animation: none!important;
    }
    .theme--pDefault.is-gold .profile-goldBorder {
        height: 100%!important;
    }
    .upgrade-quoteCar {
        bottom: 10%;
        left: 60%;
    } /* Position */
    .gridTable--raceResults.is-replay .gridTable-row:first-child:not(.is-wampus), .gridTable--raceResults .gridTable-row:first-child:not(.is-wampus) {
        background: linear-gradient(110deg,rgb(0 0 0 / 90%) 5%,#0015cf 30%)!important;
        box-shadow: 0 10px 12px 0 #2030be7a!important;
        margin-bottom: 0px!important;
    }
    .seasonLeader--default {
        background: url("https://www.nitrotype.com/dist/site/images/pages/leaderboards/season-default-bg.png") 85% 0 no-repeat,black;
    }

    /* Border Colors */
    .btn.btn--outline:not(.is-active):not(.is-disabled):not(:disabled):not(.is-success):not(.is-failure):not(:hover).btn--secondary, .seasonReward-status--claimed {
        border-color: white!important;
    } /* White Border */
    .pane-tail {
        border-color: transparent!important;
    } /* Transparent Border */
    .stickers-invCell.is-selected {
        border-color: blue!important;
    } /* Blue Border */
    .dais.is-owned, .dais.is-owned:hover {
        border: 1px white solid!important;
    } /* 1px White Border */
    .card--c {
        border: 2px white solid!important;
    } /* 2px White Border */
    .seasonReward-progress .prog-points:before {
        border-color: transparent transparent transparent #2300cf!important;
    } /* Transparent and Indigo Border */
    .garage-spot {
        border-left: 2px blue solid!important;
        border-right: none;
    } /* 2px Blue Border Left */
    .garage-spot:nth-child(15n) {
        border-right: 2px blue solid!important;
    } /* 2px Blue Border Right */
    .btn--slim, .btn--thick, .has-btn--vertical .btn--fw, .is-active.is-frozen {
        margin: 0px!important;
        border-bottom: 0.5px white solid!important;
        border-left: none;
        border-right: none;
    } /* 0.5px White Border Bottom */

    /* No Borders */
    .table--striped, .gridTable-cell, .gridTable--raceResults.is-replay .gridTable-row:first-child:not(.is-wampus), .gridTable--raceResults .gridTable-row:first-child:not(.is-wampus), .growl-close, .growl-close:hover, .notify, .notify:before, .modal-close, .modal-close:hover, .is-self:before, .achievement.is-claimable .achievement-prize {
        border: none!important;
    }
    nav {
        border-style: none!important;
    } /* No border style */
    .raceResults {
        border-radius: 0px;
    } /* 0 Border Radius */

    /* Specified Border Radii */
    .card--d {
        border-radius: 0px 15px;
    }
    .mysteryBox-btn {
        border-radius: 5px;
    }

    /* Miscellaneous */
    .profile {
        box-shadow: -1px -1px 20px blue, 10px 10px 30px #000!important;
    } 
    .achievement.is-claimable .achievement-prize {
        box-shadow: none;
    } /* Box Shadows */
    .dropdown--account .dropdown-link .icon {
        opacity: .75!important;
    } /* Opacity */
    .theme--pDefault.is-gold .profile-bg {
        width: 100%;
        height: 100%;
        padding: 2% 2% 2% 2%;
    }
}
