css:
@-moz-document domain("www.roblox.com") {
.dark-theme .content {
    background-color: #191919;
    color: #fff;
}
.dark-theme {
    background-color: #191919;
    color: #fff;
}
.dark-theme .rbx-left-col {
    box-shadow: 0 0 8px 0 rgba(0, 0, 0, .3);
    background-color: #131313;
}
.dark-theme .rbx-header {
    background-color: #131313;
    border: solid #111214;
    border-width: 0 0 1px;
}
.dark-theme .premium-landing-page .membership-section {
    background: #191919;
}
.dark-theme .dialogs .dialog-container .active .dialog-header {
    animation: none;
    background-color: #191b1d;
}
a.ad img {
    display: block;
    border: none;
    opacity: .2;
}
a.ad img:hover {
    opacity: 1;
}
.dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img {
    background-color: #191919;
}
.dark-theme .avatar .avatar-card-image {
    background-color: #191919;
}
.dark-theme .container-footer {
    background-color: #191919;
}
.game-card-container thumbnail-2d .game-card-thumb, .game-card-thumb-container .game-card-thumb {
    height: 150px;
    width: 150px;
    overflow: hidden;
    display: inline-block;
    margin: auto;
    border-radius: 8px;
    opacity: 1;
}
.dark-theme .container-empty, .dark-theme .section-content-off {
    background-color: #191919;
    color: #bdbebe;
}
}
