css:
@-moz-document domain("suckless.org") {
:root {
    --bg-color: #282a36;
    --fg-color: #f8f8f2;
    --curr-ln-color: #44475a;
    --cmt-color: #6272a4;
    --cyan: #8be9fd;
    --purple: #bd93f9;
    --pink: #ff79c6;
}

body,

#header {
    background-color: var(--bg-color);
    color: var(--fg-color);
}

#menu {
    background-color: var(--cmt-color);
}

#menu a:hover {
    background-color: var(--curr-ln-color);
}

#nav a:hover {
    background-color: var(--curr-ln-color);
}

body a {
    color: var(--cyan);
}
}
