css:
@-moz-document domain("docs.screeps.com") {
html, body {
    background: #383838;
    color: #f5f7f9;
}

a {
    color: #1d7a24;
}

a:hover {
    color: #29b734;
}

#logo-docs {
    color: #2ea434;
}

#header-main {
    border-left: 1px solid #222;
    background: #333;
    color: #e8e8e8;
}

#content-inner {
    border-left: 1px solid #222;
    background: #333;
    color: #e8e8e8;
}

.index-block__title {
    color: #1d7a24 !important;
}

.article-title {
    color: #e8e8e8;
}

.article-heading {
    color: #ccc
}

.article-content {
    color: #b8b8b8;
}

.article-content a {
    background: #333;
    color: #1d7a24;
}

.article-content a:hover {
    color: #29b734;
}

.article-content blockquote {
    border: 1px solid #555;
    background: #333;
}

.article {
    background: #333;
}

.index-block span {
    color: #ccc;
}

a.index-block {
    background: #444;
}

.sidebar-title {
    color: #fff;
}

.sidebar-link, .toc-link {
    color: #b8b8b8;
}

#article-toc-top {
    color: #777;
}

pre, code {
    color: #eee;
    background: #111;
}

.article-content strong {
    background: #333 !important;
}

.article-content table {
    border: 1px solid #222;
}

.article-content table tr:nth-child(2n) {
    background: #444;
}

.sidebar-link.current, .toc-link.current {
    color: #32d73d;
}

.sidebar-link:hover, .toc-link:hover {
    color: #4fe15a;
}

.highlight {
    background: #222;
    color: #ddd;
}

.api-link {
    background: #153c17;
}

.timeline-Widget {
    background-color: #222;
}

.timeline-Header-title {
    color: #ddd;
}

.timeline-Tweet:hover {
    background-color: #282828;
}

.TwitterCard .SummaryCard-image {
    background-color: #383838;
}

.TwitterCard-container {
    border-color: #555;
}

.TwitterCard-container--clickable:hover {
    background: #444;
}

.article-footer-prev, .article-footer-next, footer-next {
    color: #1d7a24;
}

.article-footer-prev:hover, .article-footer-next:hover, .article-footer-next:hover {
    color: #29b734;
}

#header a:hover {
    color: #29b734;
}

.article-inner {
    background: #383838;
}

.article-entry {
    color: #ddd;
}

.tocify-wrapper {
    background-color: #383838;
    border-right: 1px solid #222;
}

.tocify-wrapper li {
    color: #a8a8a8;
}  

.tocify-wrapper .back {
    background: #1d7a24;
}

.tocify-wrapper .tocify-subheader {
    background-color: #383838;
}

.tocify-wrapper .tocify-focus {
    color: #29b734;
    background-color: #444;
}

.tocify-wrapper > .search input {
    background: #222;
    border-color: #111;
    color: #29b734;
}

.tocify-wrapper > .search::before {
    margin-left: 3px;
}

.tocify-wrapper .search-results {
    background: #222;
}

.content .search-highlight {
    border: 1px solid #4cfde1;
    text-shadow: 1px 1px 0 #222;
    background: linear-gradient(to top left, #0b4f3f 0%, #3a796d 100%);
}

.content pre.nosidebar code {
    background-color: #1c201c;
}

.content pre > code {
    color: #eee;
}

.page-wrapper {
    background-color: #383838;
}

.content h1 {
    border-bottom: 1px solid #444;
    border-top: 1px solid #444;
    background-color: #282828;
}

.content h2 {
    border-top: 10px solid #282828;
    background-image: linear-gradient(to bottom, #28282888, #28282800);
}

.content table tr:nth-child(2n+1) > td {
    background-color: #222;
}

.content table tr:nth-child(2n) > td {
    background-color: #282828;
}

.content table code {
    background-color: #080808;
}

.content table th {
    color: #eee;
    background-color: #333;
    border-bottom: 1px solid #444;
}

.content table tr {
    color: #ccc;
}

.content table td {
    color: #ccc;
}

.content table td:last-child {
    color: #ccc;
}

.content table td .api-arg-title {
    color: #fff;
}

.content table td .api-arg-type {
    color: #acf;
}

.content table td a {
    color: #e8e8e8;
}

.content table td:last-child a {
    color: #e8e8e8;
}

.content table.gameplay-info tr {
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
}

.content table.gameplay-info td {
    color: #ccc;
}

.content table tr:last-child {
    border-bottom: 1px solid #444;
}

.content .api-property__cpu {
    box-shadow: 0 0 8px #333;
}

.content .api-deprecated a {
    color: #dc0000;
}

.content .api-deprecated a:hover {
    color: #ff2727;
}
}
