css:
@-moz-document domain("thegeekpage.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #050505;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #050505;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #932ebb;
        }

body {
	background: #000;
	color: #ccc;
}
.site-title a, [src='https://thegeekpage.com/wp-content/uploads/2020/11/pulpmediateam@gmail.com_.png'] {
	filter: invert(100);
}
.site-header {
	background: #000;
}
h1, h2, h3, h4, h5, h6, .entry-comments > h3, .entry-pings > h3, .comment-respond > h3 {
	color: #eee;
}
.entry-meta > *, .comment-header a {
	color: #aaa;
}
.entry-meta, .says {
	color: #808080;
}
.genesis-nav-menu a:focus, .genesis-nav-menu a:hover, .genesis-nav-menu .current-menu-item > a, .genesis-nav-menu .sub-menu .current-menu-item > a:focus, .genesis-nav-menu .sub-menu .current-menu-item > a:hover, a, .entry-title a:focus, .entry-title a:hover {
    color: #008cff;
}
.genesis-nav-menu a {
	color: #eee;
}
.post img {
    border-color: #303030;
    box-shadow: 10px 10px 5px #333;
    -moz-box-shadow: 10px 10px 5px #333;
    -webkit-box-shadow: 10px 10px 5px #333;
    -khtml-box-shadow: 10px 10px 5px #333;
}
[src='https://thegeekpage.com/wp-content/uploads/2016/02/windows-keyboard-shortcuts-1-min.jpg'], [src='https://thegeekpage.com/wp-content/uploads/2016/02/windows-keyboard-shortcuts-2-min.jpg'], [src='https://thegeekpage.com/wp-content/uploads/2016/02/windows-keyboard-shortcuts-3-min.jpg'] {
	filter: invert(100);
	border-color: #c0c0c0 !important;
    box-shadow: 10px 10px 5px #ccc !important;
    -moz-box-shadow: 10px 10px 5px #ccc !important;
    -webkit-box-shadow: 10px 10px 5px #ccc !important;
    -khtml-box-shadow: 10px 10px 5px #ccc !important;
}
tbody, td {
	border-color: #111;
}
.gsc-input-box {
	filter: invert(100);
}
.comment-respond, .entry-comments, .entry-pings {
	background: #000;
}
input, select, textarea {
	background: #000;
	color: #eee;
	border-color: #333;
}
input[type="text"] {
	color: #eee !important;
}
button, input[type="button"], input[type="reset"], input[type="submit"], .button, .genesis-nav-menu .highlight a, .more-link {
	background: #000;
	border-color: #008cff;
    box-shadow: 0 0 0 rgba(255, 255, 255, .24);
    color: #008cff;
}
.footer-widgets {
	background: #040404;
}
.entry-title a, .sidebar .widget-title a, .archive-pagination a {
	color: #eee;
}
.archive-pagination .pagination-next > a, .archive-pagination .pagination-previous > a {
	color: #008cff;
	border-color: #008cff;
}
.archive-pagination a:focus, .archive-pagination a:hover, .archive-pagination .active a {
	background: #008cff;
	color: #fff;
}
form {
	border-color: #333 !important;
}
.site-footer {
	background: #000;
}
blockquote {
	color: #aaa;
}
blockquote::before {
	color: #222;
}
.saboxplugin-wrap {
	background: #030304;
	border-color: #111;
}
.saboxplugin-wrap .saboxplugin-authorname a, .saboxplugin-wrap .saboxplugin-authorname span {
	color: #fcfcfc;
}
.archive-description {
	border-color: #111;
}
b, strong, a:focus, a:hover {
	color: #ccc;
}
}
