css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.zf.ro") {
body>div>object,body>div[style]>object,body>div[style],body>div[style]>embed,div.pub_3, #X1ExpFla11E, #X1ExpFla7E,#X1ExpFla4E,#header_pub, .pub_middle,#X1ExpFla1E,#X1FloatFla2B,#X1ExpFla11B,#X1ExpFla1B,#X1UBnr_0,#X1UBnr_1,#X1UBnr_2,#X1UBnr_3,#X1UBnr_4,#articlecontent object, #articlecontent embed, #articlecontent div embed, #articlecontent noscript, #articlecontent p br, .leaderboard, .zc_640x160_adtext, #SkyLeft, #SkyRight, .bara_up, .fb_iframe_widget, .box.cross, .zc_600x152, .zc_h30, .zc_full, .CrossFooter, #frame, .zc_h200, .zc_rectangle, #zc_rectangle_1, .ad{display:none !important}
#main_page{width:1400px !important; font:normal 14px/20px arial,helvetica,sans-serif}
#header,#main_menu,#main_menu_1,#main_menu_2,#container,#content,.content_articol,.articol_top{width:auto!important}
.content_top1{width:900px !important}
#articlecontent{font-size:16px !important; line-height:21px !important}
#articlecontent>strong>span{line-height:24px; display:block; margin:0 !important}
.articol_links{padding-left:25px}
#content .content_articol .content_top1 h1{margin:10px 0 20px !important; line-height:1.1em !important}
.articol_links{margin-top:35px !important}
.articol_top {position:relative; width:auto !important; top:-20px !important; width:900px !important}
#articlecontent{clear:left;padding-top:0 !important}
.articol_top .tools{position:absolute; right:20px}
.autor a{float:left; margin-right:15px !important}
.news_left{margin:0 20px 20px 0 !important}
.news_right{float:none !important; width:420px !important; padding-left:0 !important}
.articol_news{width:auto !important; overflow:hidden}
.articol_news p {font:normal 14px/20px arial,helvetica,sans-serif !important}
.content_top1, .content_top2 {overflow:visible !important}
.articol_news .news {float:left; width:420px; padding:0 20px 0 0 !important; min-height:200px}
#articlecontent img{padding:6px 12px 6px 0 !important;}
.article h1{line-height:30px}
.article strong,.article p{font:normal 16px/23px georgia,serif !important}
.article strong{font-weight:bold !important}
#content{width:640px!important}
}
