css:
@-moz-document url-prefix("https://cointelegraph.com/") {
/* General settings & backgrounds */
body{ background-color: #202020 !important;}


/* Article colors */
.post-content, .post-content a, .post-meta__title h1, .post-meta__item amp-timeago, .post-meta__item a, .editors-choice__list a, .post-list__item-title *, .post-list__title, .wide-slider__item-title *, .wide-slider__title *, .main-slider__navigation-item a, .post__title, .post__article, .post-content>p, .post-meta__publish-date, .post-meta__author-name{ color: #fff !important;}


/* Ads and unwanted content blocker */
div.privacy-policy.bottom-zone__privacy-policy, .sidebar__widget .banner-youtube, .post__content-shares,
.sidebar__widget:not(:first-child):not(:last-child), .ad-butler_sidebar, .post-page__content-share
{ display: none !important;}
}
