css:
@-moz-document domain("ilias.hs-karlsruhe.de") {
#ilTopBar.ilTopBar {
    padding: 0;
    height: 40px;
    top: 0;
    z-index: 1040;
    background-image: url(https://file.coffee/u/JR11nHrt8K.png);
    background-repeat: repeat-x;
}
#ilAll {
    background-image: url(https://imgur.com/8tG9hsC.png);
}

a#il_mhead_t_focus {
    color: #4AA2FF;
}

#il_center_col div.il_Block {
    background-color: #404959;
}

a.ilAccAnchor,
a.ilAccAnchor:hover {
    text-decoration: none;
    color: white;
}

.ilContainerListItemOuter {
    background-color: #5B6579;
}

.ilContainerListItemOuter:hover {
    background-color: #5293E1;
}


a.il_ContainerItemTitle:hover {
    color: #ffffff;
}

a.il_ContainerItemTitle {
    color: #ffffff;
}

.btn-group {
    color: #4AA2FF;
}

.btn-link:hover,
.btn-link:focus {
    color: #4AA2FF;
    text-decoration: underline;
    background-color: transparent;
}
.btn-link {
    color: #ffffff;
    text-decoration: underline;
    background-color: transparent;
}

#ilAdvSelListAnchorText_block_dd_pditems_0 {
    background-color: #5B6579;
}
.dropdown-toggle {
    background-color: #5293E1;
}

.ilListItemIcon {
    background-color: #ffffff;
    border-radius: 10px;
}

table.il_Block, div.il_Block {
    border-radius: 10px;
    background-color: #404959;
}

a {
    color: #5293E1;
}
a:hover {
    color: #ffffff;
}

body {
    color: #ffffff;
}

div.ilBlockInfo {
    background-color: #292F3A;    
}
.ilBlockContent .il-viewcontrol-section .btn.btn-default {
    background-color: transparent;
    border: 0;
    color: #5293E1;
    padding: 3px 3px;
}


li {
    background-color: #5B6579;
}

.nav > li > a:hover, .nav > li > a:focus {
    text-decoration: none;
    background-color: #5293E1;
}

.nav > li > a {
    text-decoration: none;
    color: #ffffff;
}

li.active > a:focus {
    background-color: #5293E1;
    color: white;
    border: 0 none;
}

#ilTopNav .navbar-nav > li > a {
    color: #ffffff;
}

#ilTopNav .navbar-nav > li > a:focus, #ilTopNav .navbar-nav > li > a:hover {
    background-color: transparent;
    color: #5293E1;
}

#ilTopNav .dropdown-menu li a {
color: #ffffff;
}
}
