css:
@-moz-document domain("theverge.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #171717;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #171717;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #e2127A;
        }

.l-root, .l-main-content {
	background: #000;
}
.c-entry-group-labels__item:nth-child(2):before {
	background: #000;
}
.c-page-title, .c-entry-hero--feature .c-page-title {
	color: #fff;
}
.p-dek, .c-byline {
	color: #9a9a9a;
}
a.c-social-buttons__more, .c-rock-list .native-ad-latest a, .c-rock-list li a, a.c-social-buttons__twitter, .c-related-list__community-name, .c-related-list__group-name:link, .c-related-list__head a:link, a.c-social-buttons__facebook, .p-input-header__input {
	color: #b2b2b2;
}
a.c-social-buttons__more svg {
	fill: #8d8d8d;
}
.c-rock-list {
	background: #000;
	color: #b2b2b2;
}
.c-tab-bar.is-minimal, .c-tab-bar.tab-bar-fixed {
	border-bottom-color: #161616;
}
.c-tab-bar {
	background: #000;
	border-color: #171718;
}
.c-tab-bar__logo {
	color: #ccc;
}
.c-tab-bar__logo svg {
	fill: #fff;
}
.e-image__meta {
	border-color: #171718;
	color: #929292;
}
body {
	background-color: #000;
	color: #b2b2b2;
}
.c-newsletter_signup_box[data-newsletter-slug=processor] .c-newsletter_signup_box__main {
    background: linear-gradient(#000,#000,#000,#1a130e);
}
.c-newsletter_signup_box[data-newsletter-slug=processor] .c-newsletter_signup_box__main .c-newsletter_signup_box__title {
	filter: invert(100);
}
.c-newsletter_signup_box__icon {
	background: #000;
}
.c-newsletter_signup_box__icon > svg > line:nth-of-type(1), .c-newsletter_signup_box__icon > svg > line:nth-of-type(2), .c-newsletter_signup_box__icon > svg > line:nth-of-type(4), .c-newsletter_signup_box__icon > svg > line:nth-of-type(5) {
	fill: #000;
	stroke: #fff;
}
.c-newsletter_signup_box__main {
	border-color: #fff;
	color: #b2b2b2;
}
.c-newsletter_signup_box--form__required-field, .c-newsletter_signup_box--form__disclaimer {
	color: #868686;
}
.c-related-list__head {
	color: #b2b2b2;
	border-bottom-color: #1f1f24
}
.c-newsletter_signup_box--form__body .p-text-input {
	border-color: #fff;
}
input {
	background: #000;
	color: #fff;
}
.p-input-header {
    background: #1f1f24;
    border-bottom-color: #171718;
}
body.editorial.user-editorial.users_edit_profile, body.editorial.user-editorial.dashboard_user, body.editorial.user-editorial.users_account {
	background: #171717 !important;
}
.m-chorus-bar .m-chorus-bar__nav>li {
	border-color: #171717;
}
.m-chorus-bar .m-chorus-bar__header {
	background: #000;
	border-color: #171717;
}
#container {
	border-color: #222;
}
.nav-main {
    background-color: #000;
    border-bottom-color: #171717;
    box-shadow: 0 3px 6px -2px rgba(255, 255, 255, .18);
}
.nav-main li a:hover {
	background: #020202;
}
.content-pane, #main, .content-pane, #tagsets {
	background: #000;
}
.editorial .content-title-bar h2, .form-page textarea {
	color: #fff;
}
.textfield, .editor_field, select, textarea, .fanshot-pane input, .calendar-select input, .p-textfield input, .calendar-date-select input, .content-title-bar, table, .have-drafts, .fs-bookmarklet {
	border-color: #1c1a18;
}
.form-page label, .form-page span.label {
	color: #bbb;
}
.form-page .form-note {
	color: #aaa;
}
.textfield, .editor_field, select, textarea, .fanshot-pane input, .calendar-select input, .p-textfield input, .calendar-date-select input {
	background: #000;
	border-color: #171717;
	color: #fff;
}
.notice {
    color: #0f0;
    background-color: #295324;
    border-color: #2f6029;
}
#main {
	border-bottom-color: #fff !important;
}
.form-page p img {
	background: #000;
	border-color: #4f5f77;
}
.m-chorus-bar .m-chorus-bar__menu {
    background-color: #000;
    border-color: #171717;
    border-top-color: #35bfa6;
}
.l-hub-wrapper {
	background: #000;
}
.c-compact-river__entry {
	background: #000;
	border-color: #171718;
}
.c-comments__recommended {
    background: #0f0b05;
}
.c-comments__comment, .c-input-header--submit-field input, .c-user-activity__entry {
	border-color: #171718;
}
.c-comments__list {
	border-top-color: #171718;
	border-left-color: #292929;
	border-right-color: #292929;
}
.c-comments__date {
	color: #929292;
}
.c-comments__message {
	background: #1f1f24;
	color: #b2b2b2;
}
.c-entry-content .p-button.disabled, .c-entry-content .p-button:disabled, .c-entry-content a.p-button.disabled, .c-entry-content a.p-button:disabled, .c-entry-content button.disabled, .c-entry-content button:disabled, .p-button.disabled, .p-button:disabled {
	background: #1f1f24;
	color: #9a9a9a;
}
.c-user-activity__modes a {
	background: #000;
}
.c-user-activity__meta, .c-user-activity__meta a.timestamp {
	color: #aaa;
}
.c-user-activity__entry:nth-child(odd) {
	background: #090803;
}
}
