css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.de"), domain("google.com"), domain("google.fr") {

* {
  font-family: -moz-use-system-font !important;

}

#res {
  color: black !important;
  font-weight: normal !important;
  font-style: normal !important;
  font-size: 12px !important;
}

#res, #res * {
  padding: 0 !important;
  margin: 0 !important;
  color: inherit !important;
  font-weight: inherit !important;
  font-style: inherit !important;
  font-size: inherit !important;
  position: static !important;
  left: 0 !important;
  top: 0 !important;
  right: 0 !important;
  bottom: 0 !important;
}

/* ========================================================================= */

a {
  text-decoration: none !important;
}

#res a {
  color: #1175B0 !important;
}

#res a:visited {
  color: #589FCF !important;
}

a:hover, #gbar .gb3 a:hover {
  text-decoration: underline !important;
  color: #F86C05 !important;
}

#res {
  padding: 10px !important;
}

h2 {
  display: none;
}

#res p:first-child {
  margin-bottom: 1em !important;
}

#res .spell {
  font-style: italic !important;
}

/* ========================================================================= */

#res li {
  padding: 1em !important;
  margin-bottom: 1.5em !important;

  border: 1px solid ThreeDShadow !important;
  -moz-border-radius: 10px !important;
  background-color: infobackground !important;
  color: -moz-fieldtext !important;
}

#res li h3 {
  font-size: 14px !important;
  font-weight: bold !important;
}

#res li h3 a {
  text-decoration: none !important;
  color: #0D5A88 !important;
}

#res li h3 a:hover {
  text-decoration: underline !important;
  color: #F86C05 !important;
}

#res li .s {
  display: block !important;
  margin-top: 0.5em !important;
  padding-top: 0.5em !important;

  max-width: none !important;

  border-top: 1px solid ThreeDShadow !important;
}

#res li h3 + span {
  display: none !important;
}

#res li .m, #res li .f {
  color: #666666 !important;
}

#res li h3 em, #res li h3 b {
  color: #B01111 !important;
}

#res li .s em, #res li .s b {
  font-weight: bold !important;
  color: #B01111 !important;
}

#res li .s cite b {
  color: #008000 !important;
}

#res cite {
  color: #008000 !important;
}

#res .gl a {
  color: #7777CC !important;
}

}
