css:
@-moz-document domain("attackofthefanboy.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #020203;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #020203;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background: linear-gradient(180deg, #fe0356, #fed700, #1c8335, #2e5ead);
        }

#top-navigation .navigation-menu-block .luminous, .tprime, .sidebar-image-holder, article {
	background: #000;
}
.different-logo-holder {
	filter: invert(100);
}
body, i.fa.fa-navicon, .vgn a, a.below-link, a.below-link:hover, .right a, .home-cards a, .vgn h3 {
	color: #fff;
}
html {
	background: #020203;
}
.content-wrap {
    background: #000;
    box-shadow: 0 2px 4px 0 rgba(255, 255, 255, .06);
}
h2.title-secondary {
	color: #9a9a9a;
}
ul.aotf-post-soc-stat-list li {
	border-color: #222;
	color: #fff;
}
.byline-date, .author-shares {
	color: #525252;
}
.vgn p {
	color: #e4e4e5;
}
.widget-title:before {
	background: #101319;
}
span.widget-text {
	background: #fff;
}
span.widget-text {
	color: #000;
}
.local-content {
	color: #666;
}
input[type=text] {
    background-image: linear-gradient(#000,#080806);
    background-color: #000;
    color: #fff;
}
textarea {
	background: #080808;
	border-color: #333;
}
.section-title, .sourced>li>a {
	color: #dedcdb;
}
.section-title:after {
	background: #161616;
}
.sidebar-image-holder {
    box-shadow: 0 2px 4px 0 rgba(255, 255, 255, .06);
}
.sidebar-image-holder a:hover {
    text-shadow: 1px 1px 0 #000;
}
.sidebar-image-holder a:hover, .sourced.large a:hover, a.focus h3:hover, .sourced>li>a:hover {
    color: #52585e;
}
.author-date {
	color: #313131;
}
.sourced p, .intro {
	color: #999ea2;
}
article.home-cards {
	border-color: #131313;
}
a.page-numbers {
	background: #000;
	color: #fff;
}
.page-numbers {
	border-color: #111;
}
.sourced.grid.thirds>li {
	background: #000;
}
.sourced>li, .sourced>li:last-child {
    box-shadow: 0 2px 4px 0 rgba(255, 255, 255, .06);
}
.intro .heading, .intro h1, .intro h2 {
	background: #020203;
}
.tags a {
	color: #737373;
	border-color: #737373;
}
.span8 > div:nth-of-type(2) {
	background: #111 !important;
}
}
