css:
@-moz-document domain("github.com") {
/* Sidebar */
html[data-octotree-theme="sidebar"][data-color-mode="auto"] .octotree-sidebar.octotree-github-sidebar .octotree-views.octotree-views {
	background-color: var(--color-bg-canvas) !important;
	color: var(--color-text-primary) !important;
	border-right: 1px solid var(--color-border-primary) !important;
}

/* Header */
html[data-octotree-theme] .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-view-header.octotree-view-header {
	background-color: var(--color-header-bg) !important;
	border-right: 1px solid var(--color-border-primary) !important;
}

/* Footer */
.octotree-sidebar.octotree-github-sidebar .octotree-footer.octotree-footer.octotree-footer {
	display: none;
}
.octotree-sidebar.octotree-github-sidebar {
	padding-bottom: 0 !important;
}

/* Links */
html[data-octotree-theme] .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view li[role].jstree-node .jstree-anchor.jstree-anchor {
	color: var(--color-text-primary) !important;
}
html[data-octotree-theme] .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view li[role].jstree-node .jstree-anchor.jstree-clicked {
	color: var(--color-header-logo) !important;
}

/* Link-icons */
.octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view .jstree-node[aria-selected="true"].jstree-leaf .octotree-default-icon.blob::before {
	color: currentcolor !important;
}

/* Selected point */
html[data-octotree-theme] .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view .jstree-wholerow-clicked.jstree-wholerow-clicked {
	background-color: var(--color-btn-bg) !important;
}

/* Hovered point */
html[data-octotree-theme] .octotree-sidebar.octotree-github-sidebar .octotree-views .octotree-tree-view .jstree-wholerow-hovered.jstree-wholerow-hovered {
background-color: var(--color-btn-hover-bg) !important;
}

/* Toggle-button */
.octotree-sidebar.octotree-github-sidebar .octotree-toggle {
	color: var(--color-btn-text) !important;
	background-color: var(--color-btn-bg) !important;
	border-color: var(--color-btn-border) !important;
	box-shadow: var(--color-btn-shadow), var(--color-btn-inset-shadow) !important;
}
.octotree-sidebar.octotree-github-sidebar .octotree-toggle > .octotree-toggle-icon::before,
.octotree-sidebar.octotree-github-sidebar .octotree-toggle > span {
	color: currentcolor !important;
}
}
