css:
@-moz-document url("https://www.roblox.com/My/Money.aspx#/#Summary_tab") {
/* script made by ThePastaNebula */

.icon-default-economy-small, .icon-robux-16x16, .icon-robux-gold-16x16, .icon-robux-gray-16x16, .icon-robux-white-16x16 {
    background-image: url(https://images.rbxcdn.com/e3fed0d20e85a51518b2756e66343a5e-economy_small_dark.svg)!important;
    background-repeat: no-repeat;
    background-size: auto auto;
    width: 20px;
    height: 20px;
    display: inline-block;
    vertical-align: middle;
}
/*
.Categories {
    margin-left:3993px;

}
*/

html {
    background-color: #232527;
}

td.Categories img.TipsyImg {
    content: url("https://i.ibb.co/xfsfPr7/download-1.png");
}




.text-footer-nav:focus, .text-footer-nav:hover {
    color: #fff;
    font-size: 20px;
    font-weight: 300;
}
.form-label {
    color:white!important;
}


a:link, a:active {
    color: #3979bf;
}

#Summary_tab .loading {
    position: absolute;
    top: 0;
    left: 1px;
    background: #232527 url(https://www.roblox.com/images/ProgressIndicator4.gif) no-repeat center;
    width: 100%;
    height: 100%;
}

a:visited {
    color:#29578a
}



.container-footer {
    background-color: #232527;
    padding: 123px;
}

.input-group-btn .input-dropdown-btn {
    background-color: #373737;
    border: 1px solid #b8b8b8;
    color: #fff;
}

.SquareTabGray.selected, .SquareTabGray.selected:hover, .SquareTabGray.ui-tabs-selected, .SquareTabGray.ui-tabs-selected:hover {
    margin-top: 0;
    padding: 9px 7px 12px 7px;
    position: relative;
    top: 1px;
    border-color: #ccc;
}

.SquareTabGray {
    list-style: none;
    float: left;
    background-color: #343434;
    padding: 7px;
    border: 1px solid #9e9e9e;
    font-weight: bold;
    font-size: 15px;
    color: #fff;
    margin: 4px 2px 0 1px;
    border-bottom-width: 0;
    position: relative;
    top: 2px;
}

.SquareTabGray.selected, .SquareTabGray.selected:hover, .SquareTabGray.ui-tabs-selected, .SquareTabGray.ui-tabs-selected:hover {
    background-color: #232527;
    margin-top: 0;
    padding: 9px 7px 12px 7px;
    position: relative;
    top: 1px;
    border-color: #ccc;
}

.form-select {
    background:#282a2d!important;
    color:white!important;
}

.SquareTabGray a {
    text-decoration: none;
    color: #fff;
    cursor: pointer;
}

.SquareTabGray a:hover {
    background: #35383c!important;
}

.btn-control, .btn-control:active, .btn-control:link, .btn-control:visited, .btn-control:hover {
    border: 1px solid #777;
    color: #fff;
    background: #232527!important;
}

.SquareTabGray a:hover {
    background: #3e3e3e!important;
}

.SquareTabGray:hover {
    background-color: #3e3e3e;
    color: #fff;
    z-index:0;
} 

.SquareTabGray.selected a:hover {
    background: #232527!important;
}

#Body {
    background-color:#232527!important;
}

#MasterContainer {
    background-color:#232527!important;    
}



.SortsAndFilters {
    background-color: #232527!important;     
}


#Summary_tab .Categories {
    background-color: #232527!important; 
    color:white!important
}

#Summary_tab .table-header th.Credit {
    background-color: #3e3e3e!important; 
    color:white!important
}

#Summary_tab .table-header th.Categories {
    background-color: #3e3e3e!important; 
    color:white!important
}

.StandardPanelContainer .StandardPanelWhite {
    background-color: #232527;
    position: relative;
    left: -5px;
    top: -1px;
    width: 100%;
    padding: 6px;
    margin-bottom: -6px;
}

#Summary_tab .RobuxColumn {
    padding-right: 6px;
    width: 390px;
        background-color: #232527;
}

h2.light {
    font-size: 16px;
    font-weight: normal;
    color: #fff;
}

.body, .text, pre {
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    color: #fff;
    font-size: 14px;
    line-height: 1.428;
}









}
