css:
@-moz-document url-prefix("https://www1.flightrising.com/hoard"), url-prefix("https://www1.flightrising.com/vault") {
.hoard-result-item-box[data-protected="1"] .hoard-result-item-icon, .hoard-result-item-box[data-protected="1"] .virtual-hoard-result-item-icon {
    position: relative;
    opacity: 1;
}
.hoard-result-item-box[data-protected="1"] .hoard-result-item-controls {
    opacity: 1;
}
.hoard-result-item-box[data-protected="1"] .hoard-result-item-controls select, .hoard-result-item-box[data-protected="1"] .common-checkbox {
    background-color: #e7c45f;
    border-color: #803f08;
    color: #000;
}
.hoard-result-item-box[data-protected="1"] .hoard-result-item-icon::before, .hoard-result-item-box[data-protected="1"] .virtual-hoard-result-item-icon::before {
    position: absolute;
    content: '';
    height: 29px;
    width: 27px;
    background: url('https://i.postimg.cc/Kj5XDmVg/lock-small.png') no-repeat center;
    background-size: cover;
    z-index: 1;
    pointer-events: none;
    top: -11px;
    right: -10px;
}
.hoard-result-item-box[data-protected="1"][data-favorite="1"]::after {
    right: 16px;
}
.hoard-result-item-box[data-favorite="1"]::after {
    pointer-events: none;
}
.common-checkbox[data-selected="1"]::after {
    filter: brightness(0);
}
}
