/**
 * mtness style: firebug
 *
 * written with the left Hand of mtness http://userstyles.org/users/3613
 * many thanx to nail (http://userstyles.org/users/16135) for finding the right url-prefix! 
 */ /* i really want this to be global */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("view-source:")
{

	html
	{
		background-color:#000 !important;
	}
	body
	{
		font-family:'Lucida Console','Monaco' !important;
		color: green !important;
		font-size: 12px !important;
		font-weight: normal !important;
		white-space: pre !important;
		margin: 0 !important;
		padding: 4px !important;
		min-height: 100% !important;
	}
	.start-tag,
	.end-tag
	{
		color:#0f0 !important;
		font-weight:normal !important;
	}
	.comment
	{
		color: yellow !important;
		font-style: italic !important;
		font-style:normal !important;
	}
	.doctype
	{
		color: #DD80DD !important;
		font-style:normal !important;
	}
	.cdata
	{
		color: #CC0066 !important;
	}
	.pi
	{
		color: orchid !important;
		font-style: italic !important;
	}
	.entity
	{
		color:#FF4500 !important;
		font-weight: normal !important;
	}
	.attribute-name
	{
		color: #00ff00 !important;
		font-weight:normal !important;
	}
	.attribute-value
	{
		color: steelblue !important;
		font-weight:normal !important;
	}
	.summary
	{
		display: block !important;
		background-color: #FFFFCC !important;
		width: 90% !important;
		border: solid !important;
		border-width: 1pt !important;
		font-family: sans-serif !important;
	}
	.popup
	{
		font-weight: normal !important;
	}
	.markupdeclaration
	{
		color: steelblue !important;
		font-style: italic !important;
	}
	.error
	, .error > .start-tag
	, .error > .end-tag
	, .error > .comment
	, .error > .cdata
	, .error > .doctype
	, .error > .pi
	, .error > .entity
	/* , .error > .attribute-name */
	, .error > .attribute-value {
	  color: red !important;
	}
	.error > .attribute-name
	{
	  color: green !important;
	}

	*|*:link { color:#00f !important; }
	a { text-decoration: none !important; }
	a:hover { color: #ffffff !important; }
	a:visited { color: #8fa3ff !important; }

}
