css:
@-moz-document url-prefix("https://www.roblox.com/My/Money.aspx#/#") {

/* Script by ThePastaNebula (credit is appreciated but not necessary) */

html {
    background: #232527;
}

#MasterContainer {
    background: #232527;
}

.container-footer {
    background: #232527;
}

#Body {
    background: #232527 !important;
}

.MyMoneyPage #TabsContentContainer {
    background: #232527;
    color: white;
}

.icon-default-economy-small, .icon-robux-16x16, .icon-robux-gold-16x16, .icon-robux-gray-16x16, .icon-robux-white-16x16 {
    background-image: url(https://images.rbxcdn.com/e3fed0d20e85a51518b2756e66343a5e-economy_small_dark.svg);
}

.form-label {
    color: #fff;
}

.table-header th {
    background-color: #3e3e3e;
}

.form-select {
    background-color: #3e3e3e;
    color: white;
}

.btn-control.btn-control-small {
    background: #3e3e3e;
    color: white;
}

#MyTransactions_tab .loading, #TradeItems_tab .loading {
    background: #232527 url(https://www.roblox.com/images/ProgressIndicator4.gif) no-repeat center;
}

#Summary_tab .loading {
    background: #232527 url(https://www.roblox.com/images/ProgressIndicator4.gif) no-repeat center;
}

.text-footer-nav:focus, .text-footer-nav:hover {
    color: #fff;
}

.footer .footer-link .text-footer-nav.privacy:hover {
    color: #00c8ff;
}

.input-group-btn .input-dropdown-btn, .input-group-btn .input-dropdown-btn {
    background-color: #3e3e3e;
    border-color: #ffffff;
    color: #fff;
}

.input-group-btn .input-dropdown-btn:focus, .input-group-btn .input-dropdown-btn:hover {
    background-color: #4e4e4e;
    border-color: #ffffff;
    color: #fff;
    cursor: pointer;
}

.btn-default.active, .btn-default:active, .btn-default:focus, .btn-default:hover, .open>.btn-default.dropdown-toggle {
    color: #ffffff;
    background-color: #4e4e4e;
    border-color: #ffffff;
}

.open .dropdown-menu {
    background-color: #4e4e4e;
}

.open .dropdown-menu a:link {
    color: #fff;
}

a:link, a:visited, a:active {
    color: #376fae;
}

#Summary_tab .RobuxColumn, #Summary_tab .TicketsColumn {
    background-color: #232527;
}

h2.light {
    font-size: 16px;
    font-weight: normal;
    color: #ffffff;
}

.Categories img.TipsyImg {
    content: url("https://i.ibb.co/LR1KgrP/tooltip-darktheme.png");
}

.SquareTabGray.selected:hover {
    background-color: #232527 !important;
}

.SquareTabGray.selected:hover a {
    background-color: #232527 !important;
}

.SquareTabGray.selected, .SquareTabGray.selected:hover, .SquareTabGray.ui-tabs-selected, .SquareTabGray.ui-tabs-selected:hover {
    background-color: #232527;
}

.SquareTabGray.selected a {
    background-color: #232527 !important;
    color: #ffffff;
}

.SquareTabGray {
    background-color: #3e3e3e;
}

.SquareTabGray a {
    background-color: #3e3e3e !important;
    color: #ffffff;
}

.SquareTabGray:hover {
    background-color: #4e4e4e !important;
}

.SquareTabGray:hover a {
    background-color: #4e4e4e !important;
}

.MyMoneyPage td.Member div.Roblox {
    background: url(https://svgur.com/i/SEG.svg) no-repeat 50%/18px;
}

.icon-additem, .icon-arrow-right-blue, .icon-audio, .icon-back, .icon-bigplay, .icon-bigplay-fill, .icon-bigstop, .icon-bigstop-fill, .icon-brokenpage, .icon-carousel-left, .icon-carousel-right, .icon-chat-gray, .icon-check-selection, .icon-checkmark, .icon-checkmark-blue, .icon-checkmark-on, .icon-checkmark-white, .icon-checkmark-white-bold, .icon-clock, .icon-close, .icon-close-white, .icon-copy, .icon-default-generic, .icon-down, .icon-edit, .icon-female, .icon-female-white, .icon-first-gray, .icon-first-page, .icon-games-carousel-left, .icon-games-carousel-left.hover, .icon-games-carousel-right, .icon-games-carousel-right.hover, .icon-globe, .icon-grid, .icon-home, .icon-inactive, .icon-last-gray, .icon-last-page, .icon-left, .icon-left-gray, .icon-locked, .icon-logo-apple, .icon-logo-win, .icon-male, .icon-male-white, .icon-menu, .icon-menu-black, .icon-minus, .icon-more, .icon-more-gray-vertical, .icon-moreinfo, .icon-next, .icon-notifications, .icon-page, .icon-password-hide, .icon-password-hide-white, .icon-password-show, .icon-password-show-white, .icon-pastname, .icon-pause, .icon-pause-fill, .icon-play, .icon-playgame, .icon-plus, .icon-regenerate, .icon-right, .icon-right-gray, .icon-sandglass, .icon-search, .icon-settings, .icon-slideshow, .icon-sorting, .icon-trash-bin, .icon-unlocked, .icon-up, .icon-viewdetails {
    background-image: url(https://images.rbxcdn.com/a057a8bc94e7ab78517765ddb4e77384-generic_dark_11062018.svg);
}

.icon-checkmark-16x16, .icon-close-16x16, .icon-close-gray-16x16, .icon-default-generic-16x16, .icon-down-16x16, .icon-edit-16x16, .icon-left-16x16, .icon-moreinfo-16x16, .icon-red-timer-16x16, .icon-right-16x16, .icon-settings-16x16, .icon-up-16x16, .input-group-btn .input-dropdown-btn:focus .icon-down, .input-group-btn .input-dropdown-btn:hover .icon-down, .open .input-dropdown-btn .icon-down, .rbx-select-group:hover .icon-down {
    background-image: url(https://images.rbxcdn.com/a057a8bc94e7ab78517765ddb4e77384-generic_dark_11062018.svg);
}
}
