css:
@-moz-document domain(thefewgoodmen.com) {

/* Reduce ridiculous header image space. Note: in the past 
shrinking the images was possible now it seems it can only 
be truncated :-( */
.renderlogo {
	display: none;
}

#third {
	display: none;
}

.header_tops {
	height: 45px;
}

.background-css-build {
	height: calc(110px) !important;
}

.background-css-build {
	background-position-y: 0px;
}

.p-breadcrumbs {
	margin-bottom: 0px;
}

/* Fix post edit toolbar */
.fr-box.fr-basic .fr-toolbar.fr-top {
	background: #eeeeee;
}

/* Reduce wasted space between entries in the forum list */
.node-body-empty {
	padding: 2px;
}

/* Reduce wasted space between thread entires in the thread list */
.structItem {
	padding: 2px;
}

.structItem-cell {
	padding: 2px 8px;
}

/* Reduce wasted space in the header area of the forum list */
.p-body-header {
	margin-bottom: 2px;
}
.block-outer {
	padding-bottom: 2px;
}
.p-description {
	margin-bottom: 5px;
}
.p-body-inner {
	padding-top: 5px;
	padding-bottom: 10px;
}

/* Reduce wasted space around main menu */
.p-nav-list .p-navEl-link, .p-nav-list .p-navEl-splitTrigger {
	padding-top: 3px;
	padding-bottom: 3px;
}
.p-nav-opposite .p-navgroup-link {
	padding: 3px 10px;
}

/* Redue wasted space below the sub forum list on the thread list */
.block {
	margin-bottom: 10px;
}

/* Reduce wasted space around filer bar on the thread list */
.block-filterBar {
	padding: 2px 10px;
}
}
