@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("huffingtonpost.com") {
/* removes all of the crap from the story pages */
#most_popular, .cont_all_news_module, #buzzfeed, #twitter_div, #blogroll_wrapper, .b_n_pages-fp, #top_news_inner, .CIU-entries, .rbox-green-title, .rbox-bn-fp, .contin_below, #ad_advertisement, #sit_huffpo_logged, #sit_huffpo_not_logged, .ad_block, #curtainunit, #etp_advert, .top_rule, .forma_email, #hp_social_network, #chicklets, .facebookvote, .header-search-bp, #comments-hint, .login-menu, #logo_bpages, .ad_728_90b, .header-line, .clear, #must_be_logged_in, .contextual_ad_unit, #around_the_web, .grid.third.flush_top.threeup_entries, #threeup_left_nav, #threeup_right_nav, #threeup_wide_container, .mastheader, .blogger_menu_content, #sidebar_right, .read_more, #topnav_big_news_module, #top_nav, .read_more_top_blog, .sidebarHeader, #comments-header, .digg_header, .digg_wrap, #related_images, #footer_nav_1, #footer_nav_2, #footer_nav_links, #searchFieldF, .searchButton, #blogger_menu_email_up, #blogger_menu_becomefan, .about_reporter_rss, P[style="font-size: large;"], IMG[alt="iPhone App Promo"], #twitter_content, .twitter_search_header, #bing_module, #bing_content, .bing_pixie, #service_bottom_bar
  {
     display: none !important; 
  }
/* makes the page wider */
#blog_content, .entry, #news_content, .col.entry_right.full, .about_reporter_name_email
  {
     width: 100% !important;
  }
/* everything in Segoe UI or Tahoma */
body, td, input, textarea, select, legend, button, option, div.s, font {
    font-family:       "Segoe UI", Tahoma         !important;
  }
}
