css:
@-moz-document domain('tradera.com') {

body, #site-main, .site-main, .bp .bg-white, .slim-header { background-color: #111 !important; }


body, p, a, h1, h2, h3, h4, h5, h6, div, form { color: #fff !important; }
/**:not(svg) { color: #fff !important; }*/
.bp .bg-gray-100 { background-color: #282828 !important; }
.bp .bg-gray-200 { background-color: #222 !important; }
.bp .text-gray-600 { color: #bbb !important; }
.bp .text-gray-800 { color: #ddd !important; }
.bp .btn { color: #fff; }
.bp .btn:hover { color: #eee; }
.bp .btn-toggle, .btn-toggle { background-color: #000; color: #fff; }
a:link { color: #aaf !important; }
a:visited { color: #f8f !important; }
.bp .text-primary { color: #8bf !important; }
.bp .bg-light { background-color: #0a0a0a !important; }
.bp .border-bottom, .bp .border-top { border-color: #444 !important; }
.bg-white { background: #000 !important; }
input::placeholder { color: #bbb; }
textarea { background-color: #000; color: #fff; }
.default-action, .favorite-action, .form-select, .shop-item-settings-action, .toggle-action {
    background-color: #111;
    color: #fff;
}

.bp .bg-branded { background-color: #220 !important; }
.bp .badge-branded { background-color: #780; }


.item-card-new:hover { background-color: #333; }
.item-card-new .item-card-image.item-card-image-loaded { background-color: #111; }
.item-card-new.item-card-new-highlight {
    background-color: #222800;
    border-color: #222;
}


.accordion .accordion-item { background-color: #333; }
.accordion .accordion-item:hover { background-color: #444; }
.site-dropdown__hamburger { background-color: #111 !important; }
.site-dropdown {
    background-color: #000;
}
.site-dropdown .site-dropdown__item:focus, .site-dropdown .site-dropdown__item:hover {
    background-color: #780;
}
.site-dropdown .site-dropdown__separator {
    background-color: #111;
}


.bp .nav-tabs { background-color: #000; }
.bp .form-control {
    background-color: #000;
    /*border: none !important;*/
    border-color: #aaa;
    color: #ccc;
}
.bp .form-control:focus {
    background-color: #444;
    color: #eee;
}
.bp .form-control-radio-switch {
    background-color: #222;
    border: 1px solid #444;
}
.bp .form-control-radio-switch .form-check-input:checked + .form-check-label {
    background-color: #fff;
    color: #000;
}
.bp select.form-control {
    background-color: #000;
    border: 1px solid #aaa !important;
}
.bp .form-control::placeholder, .bp .form-control:hover::placeholder {
    color: #aaa;
}
.bp .btn-link-branded.active, .bp .btn-link-branded:active, .bp .btn-link-branded:focus {
    color: #000 !important;
}
.bp .alert-info { background-color: #333; }
.alert-info { background-color: #330; }
.site-dropdown .site-dropdown__item-expandable.site-dropdown__item-expanded > button > .site-dropdown__item {
    background-color: #444;
}
.site-dropdown .site-dropdown__transparent-btn:focus, .site-dropdown .site-dropdown__transparent-btn:focus-within {
    background-color: #780;
}
.hover-menu-container .hover-menu-area {
    background-color: #000;
}
.hover-menu-container .hover-menu-area .hover-menu__item:hover {
    background-color: #780;
}
.hover-menu-container .hover-menu-area .hover-menu__item-seperator { /* sic "seperator" */
    background-color: #111;
}

.form-control-radio-switch .form-check-label { border-color: #666;}


.slim-header__search-category.slim-header__search-category--default-selected {
    background-color: #780;
}
.slim-header__search-category {
    background-color: #393939;
}
.slim-header__search-suggestions .list-group {
    background-color: #111;
}
.slim-header__search-suggestions .slim-header__search-suggestion {
    background-color: #222;
    color: #fff !important;
}
.site-dropdown__hamburger.active, .site-dropdown__hamburger.is-active, .site-dropdown__hamburger:active, .site-dropdown__hamburger:focus {
    background-color: #780 !important;
    border-color: #780 !important;
    color: #fff;
}

svg { color: #000 !important; filter: invert(); }
.hamburger-inner, .hamburger-inner::after, .hamburger-inner::before {
    background-color: #fff;
}
.hamburger.is-active .hamburger-inner, .hamburger.is-active .hamburger-inner::after, .hamburger.is-active .hamburger-inner::before {
    background-color: #fff;
}

.loading-portal { background-color: #333; }
.bp .animation-loading-shimmer {
    -webkit-animation-duration:1s;
    animation-duration:1s;
    -webkit-animation-fill-mode:forwards;
    animation-fill-mode:forwards;
    -webkit-animation-iteration-count:infinite;
    animation-iteration-count:infinite;
    -webkit-animation-name:loadingShimmer;
    animation-name:loadingShimmer;
    -webkit-animation-timing-function:linear;
    animation-timing-function:linear;
    background:#fff;
    background:-webkit-linear-gradient(left,#fff 8%,#f0f1f5 18%,#fff 33%);
    background:-webkit-gradient(linear,left top,right top,color-stop(8%,#fff),color-stop(18%,#f0f1f5),color-stop(33%,#fff));
    background:linear-gradient(90deg,#111 8%,#222 18%,#111 33%);
    background-size:400% 100%;
    position:relative
}

.bp .form-control-radio-switch .form-check-input:checked + .form-check-label {
    background-color: #fff !important;
}
.bp .form-control-radio-switch .form-check-input:checked + .form-check-label svg {
    filter: none;
}


.myt-page-without-top-gutter .myt-page-content { background-color: #000; }
.myt-page-without-top-gutter .myt-page-header { background-color: #000; }
.myt-menu, .toolbar, .profile-page-feedback-summary, .profile__nav, .profile-page-feedback, .feedback-items { background: #000; }
.myt-tab { background-color: #111; }
.myt-pick-list-filter-bar { background-color: #222; }
.profile-page-feedback-summary-table tbody tr:nth-child(2n+1) { background-color: #181818; }
.profile-tabs li { background-color: #0a0a0a; }
.myt-list-feedback-link { background-color: #000; }
.threads-wrap { background-color: #000; }
.form-control-radio-switch { background-color: #000; }
.conversation-message--other-user { background-color: #000; }
.threads-wrap .thread-items .thread-item.thread-item-active { background-color: #340; }
.form-control:disabled, .form-control[readonly] { background-color: #444; }
.thread-item-ACTIVE .text-primary { color: #aaf !important; }
.thread-item-ACTIVE .text-gray-600 { color: #aaa !important; }


.image-gallery-item { background-color: #181818; }
.feature-feedback-dialog { background-color: #000; }
.feature-feedback-status-like .feature-feedback-dialog-like-label, .feature-feedback-status-dislike .feature-feedback-dialog-dislike-label { color: #eee; }


img[src="/static/images/shipping/schenker.svg"] { filter: invert(); }
.shipping-schenker-upsell-schenker-logo { background-color: #000; }
.shipping-schenker-upsell-content { background-color: #220; }
.advanced-option-transparent-layer { background: hsla(0,0%,0%,.8); }



*[class^="pika"] { background-color: #000; }
.pika-single.tradera-theme td.is-disabled .pika-button, .pika-single.tradera-theme td.is-today.is-disabled .pika-button { background-color: #000; }
.pika-single.tradera-theme td.is-today .pika-button, .pika-single.tradera-theme td .pika-button { background-color: #181818; }
.pika-single.tradera-theme td.is-inrange .pika-button, .pika-single.tradera-theme td.is-today.is-inrange .pika-button { background-color: #331; color: #fff; }
.myt-list-navigation-item::before, .myt-nav-list-item a::after, .pika-single.tradera-theme .pika-next::before, .pika-single.tradera-theme .right.pika-next::after, .right.myt-list-navigation-item::after, .pika-single.tradera-theme .pika-prev::before, .pika-single.tradera-theme .right.pika-prev::after { color: #fff; }



.redactor__area .redactor-editor, .redactor__area .redactor-in { background-color: #000; }



.site-pagename-Brand .site-main, .site-pagename-Category .site-main, .site-pagename-CategoryList .site-main, .site-pagename-CustomResults .site-main, .site-pagename-EndlessRecommendation .site-main, .site-pagename-how-to-buy .site-main, .site-pagename-how-to-sell .site-main, .site-pagename-Landing .site-main, .site-pagename-OrderCheckout .site-main, .site-pagename-PayeeVerification .site-main, .site-pagename-PayeeVerificationCompleted .site-main, .site-pagename-SearchResults .site-main, .site-pagename-SellerItems .site-main, .site-pagename-SellItem .site-main, .site-pagename-ShopSellerItems .site-main, .site-pagename-StartPage .site-main, .site-pagename-TraderaPay .site-main, .site-pagename-verify-bankid .site-main, .site-pagename-VIP .site-main { background-color: #000 !important; }

}
