css:
@-moz-document url-prefix("https://thehackernews.com") {
/* Backgrounds */
body, .main, .below-post, .deals, footer{ background-color: #202020 !important;}


/* Text colors */
.main, .story-title a, .latest-title, .author, .author a, footer, .sb-text, .footer-box-h4, .footer-box-h5, .f-menu-list li a{ color: #fff !important;}
div.latest-h3, .home-title{color: #356ae6 !important;}


/* AdBlocker */
.adsbygoogle, .ad_two, .ad_three, div.google.cf, .zoho-box, .clear.av-side-box.sidefix, .sidebar a[rel="nofollow noopener sponsored"], .float-share{ display: none !important;}
}
