css:
@-moz-document domain("record.com.mx") {
@media (min-device-width: 320px) and (min-width: 320px),
(max-device-width: 320px) and (min-width: 320px) and (orientation: landscape) {
    #section-header #zone-menu-wrapper,
    #zone-menu-wrapper #zone-menu #region-menu #block-menunotmusa2-menunotmusa2 .clearfix .content,
    #zone-menu-wrapper #zone-menu #region-menu #block-menunotmusa2-menunotmusa2 .clearfix .content #MenuHorizontalPadre {
        background-color: #000;
    }

    .node-type-article #section-content #zone-content-wrapper #zone-content #region-content .field-name-title,
    .node-type-article #section-content #zone-content-wrapper #zone-content #region-content .field-name-field-teaser,
    .node-type-article #section-content #zone-content-wrapper #zone-content #region-content .field-name-body,
    .node-type-article #section-content #zone-content-wrapper #zone-content #region-content .field-name-camino-de-migas-dn a,
    .node-type-article #section-content #zone-content-wrapper #zone-content #region-content .field-name-notas-relacionadas-article .field-item-related .field-related-title a,
    [class*='page-'] #section-content #zone-postscript-wrapper #zone-postscript #region-postscript-first .contentScrollX [class*="views-row-"] .views-field-field-teaser a {
        color: #fff;
    }
    .node-type-article #section-content #zone-content-wrapper #zone-content #region-content .field-name-autor-y-fecha,
    [class*='page-'] #section-content #zone-postscript-wrapper #zone-postscript #region-postscript-first .contentScrollX [class*="views-row-"] .views-field-title a {
        color: #eee;
    }
}

.zone-menu-wrapper,
.zone-preface-wrapper,
.zone-content-wrapper,
.zone-postscript-wrapper {
    background-color: #000;
}

.zone-preface,
.zone-content,
.zone-postscript,
.region-preface-first,
.region-preface-second,
.region-preface-third {
    background-color: #333;
}

.field {
    color: #fff;
}

.views-field-field-im-gen-principal > .field-content {
    border: 2px solid;
}
}
