css:
@-moz-document domain("showrss.info") {
body {
	font-family: "Lato","Helvetica Neue",Helvetica,Arial,sans-serif;
	font-size: 15px;
	line-height: 1.42857143;
	color: #7a7a7a;
	background-color: #000;
}
.sh {
	color: #b8b8b8;
}
.set {
	color: #a70000;
	font-size: 12px;
}
.list-group-item {
	position: relative;
	display: block;
	padding: 10px 15px;
	margin-bottom: -1px;
	background-color: #000;
	border: 1px solid #343434;
}
legend {
	display: block;
	width: 100%;
	padding: 0;
	margin-bottom: 21px;
	font-size: 22.5px;
	line-height: inherit;
	color: #fff;
	border: 0;
	border-bottom: 1px solid transparent;
}

}
